// src/components/Dashboard.js
import { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import ProgressChart from './ProgressChart';
import ExportButtons from './ExportButtons';
import API from '../services/api';

const Dashboard = ({ user }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const { data } = await API.get('/projects');
      setProjects(data || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const isProf = user?.role === 'admin';

  if (loading) return <div className="text-center p-10 text-2xl">Chargement...</div>;

  return (
    <div className="container mx-auto p-8 min-h-screen bg-gray-50">
      <h1 className="text-4xl font-bold text-center text-indigo-800 mb-4">
        {isProf ? 'Espace Professeur' : `Bienvenue, ${user?.name}`}
      </h1>
      <p className="text-center text-gray-600 mb-10 text-lg">
        {isProf ? 'Gérez vos projets et suivez les rendus' : 'Consultez et déposez vos comptes-rendus'}
      </p>

      {/* UN SEUL ProjectList → UN SEUL BOUTON */}
      <ProjectList 
        projects={projects} 
        user={user} 
        refreshProjects={fetchProjects} 
      />

      {/* Statistiques pour le prof */}
      {isProf && (
        <div className="mt-16 bg-white p-10 rounded-3xl shadow-2xl">
          <h2 className="text-3xl font-bold text-center mb-8">Statistiques</h2>
          <div className="grid md:grid-cols-2 gap-10">
            <ProgressChart projects={projects} />
            <ExportButtons projects={projects} />
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
