// src/components/ProjectList.js ‚Üí VERSION STYLE AM√âLIOR√â
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [studentModal, setStudentModal] = useState(false);
  const [profModal, setProfModal] = useState(false);
  const [editProject, setEditProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const isProf = user?.role === 'admin';

  const mySubmission = (project) => {
    if (!project || !project.submissions || !user?._id) return null;
    return project.submissions.find(s => {
      const studentId = s?.student?._id || s?.student;
      return studentId?.toString() === user._id.toString();
    });
  };

  const openStudentModal = (project) => {
    if (!project) return;
    setSelectedProject(project);
    const submission = mySubmission(project);
    setFileName(submission?.customName || '');
    setFile(null);
    setStudentModal(true);
  };

  const submitStudentFile = async () => {
    if (!file) return alert('Choisissez un fichier');
    if (!fileName.trim()) return alert('Donnez un nom au fichier');
    if (!selectedProject) return alert('Erreur: projet non s√©lectionn√©');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('üéâ Rendu d√©pos√© avec succ√®s !');
      setStudentModal(false);
      setSelectedProject(null);
      refreshProjects();
    } catch (err) {
      console.error('Erreur d√©p√¥t:', err.response?.data || err);
      alert(err.response?.data?.msg || 'Erreur lors du d√©p√¥t');
    }
  };

  const openProfModal = (project = null) => {
    setEditProject(project);
    setProfModal(true);
  };

  const deleteProject = async (projectId) => {
    if (window.confirm('√ätes-vous s√ªr de vouloir supprimer ce projet ? Cette action est irr√©versible.')) {
      try {
        await API.delete(`/projects/${projectId}`);
        refreshProjects();
      } catch (err) {
        console.error('Erreur suppression:', err);
        alert('Erreur lors de la suppression');
      }
    }
  };

  if (!Array.isArray(projects)) {
    return (
      <div className="text-center py-20">
        <div className="bg-white/80 backdrop-blur-lg rounded-3xl p-12 shadow-2xl border border-white/30 max-w-md mx-auto">
          <div className="text-6xl mb-4">üìö</div>
          <p className="text-gray-600 text-xl font-medium">Aucun projet disponible</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-16">
      {/* Bouton Cr√©er (prof) - STYLE AM√âLIOR√â */}
      {isProf && (
        <div className="text-center">
          <button
            onClick={() => openProfModal()}
            className="group px-14 py-6 bg-gradient-to-r from-white to-indigo-50 text-indigo-600 font-bold text-xl rounded-2xl shadow-2xl hover:shadow-3xl transition-all duration-300 transform hover:scale-105 border-2 border-indigo-100 hover:border-indigo-200"
          >
            <span className="flex items-center justify-center gap-3">
              <span className="text-2xl group-hover:scale-110 transition-transform">‚ú®</span>
              Cr√©er un nouveau projet
              <span className="text-2xl group-hover:scale-110 transition-transform">üöÄ</span>
            </span>
          </button>
        </div>
      )}

      {/* Cartes - STYLE AM√âLIOR√â */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {projects.map((project) => {
          if (!project) return null;
          
          const submission = mySubmission(project);
          const isDeadlinePassed = new Date(project.deadline) < new Date();
          const daysLeft = Math.ceil((new Date(project.deadline) - new Date()) / (1000 * 60 * 60 * 24));

          return (
            <div key={project._id} className="group bg-white/90 backdrop-blur-sm rounded-3xl shadow-2xl overflow-hidden transform hover:scale-105 transition-all duration-500 border border-white/50 hover:shadow-3xl">
              {/* Header avec gradient am√©lior√© */}
              <div className={`relative p-8 text-white overflow-hidden ${
                isDeadlinePassed 
                  ? 'bg-gradient-to-br from-rose-500 to-pink-600' 
                  : daysLeft <= 3 
                    ? 'bg-gradient-to-br from-orange-500 to-amber-600'
                    : 'bg-gradient-to-br from-indigo-600 via-purple-600 to-violet-600'
              }`}>
                <div className="absolute inset-0 bg-black/10"></div>
                <div className="relative z-10">
                  <h3 className="text-2xl font-bold mb-3 drop-shadow-lg">{project.name}</h3>
                  <div className="flex items-center justify-between">
                    <p className="text-lg opacity-95 font-medium">
                      üìÖ {new Date(project.deadline).toLocaleDateString('fr-FR')}
                    </p>
                    {!isDeadlinePassed && daysLeft <= 7 && (
                      <span className="px-3 py-1 bg-white/20 rounded-full text-sm font-semibold backdrop-blur-sm">
                        {daysLeft} jour{daysLeft > 1 ? 's' : ''}
                      </span>
                    )}
                  </div>
                  {isDeadlinePassed && (
                    <div className="flex items-center gap-2 mt-3 px-3 py-1 bg-white/20 rounded-full text-sm font-semibold backdrop-blur-sm w-fit">
                      <span>‚ö†Ô∏è</span>
                      <span>Deadline d√©pass√©e</span>
                    </div>
                  )}
                </div>
              </div>

              {/* Contenu de la carte */}
              <div className="p-8 space-y-6">
                {/* Description */}
                <div className="min-h-[80px]">
                  <p className="text-gray-700 text-lg leading-relaxed">
                    {project.description || 'üìù Aucune description fournie'}
                  </p>
                </div>

                {/* PDF du sujet */}
                {project.pdf && (
                  <a 
                    href={`http://localhost:5000/${project.pdf}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="inline-flex items-center gap-3 px-6 py-3 bg-gradient-to-r from-blue-50 to-indigo-50 text-indigo-700 font-semibold rounded-xl hover:shadow-lg transition-all duration-300 border border-blue-100 hover:border-blue-200 group"
                  >
                    <span className="text-lg">üìÑ</span>
                    <span>T√©l√©charger le sujet</span>
                    <span className="group-hover:translate-x-1 transition-transform">‚Üí</span>
                  </a>
                )}

                {/* Section √âtudiant */}
                {!isProf && (
                  <div className="pt-6 border-t border-gray-100">
                    {submission ? (
                      <div className="bg-gradient-to-r from-emerald-50 to-green-50 rounded-2xl p-6 text-center border-2 border-emerald-200 shadow-lg">
                        <div className="text-4xl mb-3">‚úÖ</div>
                        <p className="font-bold text-emerald-800 text-lg mb-2">Rendu d√©pos√© !</p>
                        <p className="text-gray-700 font-medium text-sm mb-3">{submission.customName}</p>
                        <a 
                          href={`http://localhost:5000/${submission.file}`} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="inline-flex items-center gap-2 px-4 py-2 bg-emerald-600 text-white text-sm rounded-lg hover:bg-emerald-700 transition-all mb-3"
                        >
                          <span>üì•</span>
                          T√©l√©charger
                        </a>
                        {!isDeadlinePassed && (
                          <button 
                            onClick={() => openStudentModal(project)} 
                            className="block w-full mt-3 text-indigo-600 underline font-semibold hover:text-indigo-700 transition"
                          >
                            Modifier le rendu
                          </button>
                        )}
                      </div>
                    ) : (
                      <button 
                        onClick={() => openStudentModal(project)}
                        disabled={isDeadlinePassed}
                        className={`w-full py-5 text-white font-bold text-lg rounded-2xl shadow-xl transition-all duration-300 transform hover:scale-105 ${
                          isDeadlinePassed 
                            ? 'bg-gradient-to-r from-gray-400 to-gray-500 cursor-not-allowed opacity-70' 
                            : 'bg-gradient-to-r from-indigo-600 to-purple-600 hover:shadow-2xl hover:bg-gradient-to-r hover:from-indigo-700 hover:to-purple-700'
                        }`}
                      >
                        <span className="flex items-center justify-center gap-3">
                          <span className="text-xl">üì§</span>
                          {isDeadlinePassed ? 'Deadline d√©pass√©e' : 'D√©poser mon rendu'}
                        </span>
                      </button>
                    )}
                  </div>
                )}

                {/* Section Professeur */}
                {isProf && (
                  <div className="pt-6 border-t border-gray-100 space-y-6">
                    {/* Compteur de rendus */}
                    <div className="text-center">
                      <div className="bg-gradient-to-r from-purple-50 to-violet-50 rounded-2xl p-4 border-2 border-purple-200">
                        <p className="text-3xl font-bold text-purple-700 mb-1">
                          {project.submissions?.length || 0}
                        </p>
                        <p className="text-purple-600 font-semibold">
                          rendu{project.submissions?.length !== 1 ? 's' : ''} re√ßu{project.submissions?.length !== 1 ? 's' : ''}
                        </p>
                      </div>
                    </div>

                    {/* Liste des rendus √©tudiants */}
                    {project.submissions && project.submissions.length > 0 && (
                      <div className="bg-gradient-to-r from-purple-50 to-violet-50 rounded-2xl p-5 border-2 border-purple-200 shadow-inner">
                        <h4 className="font-bold text-purple-800 text-lg mb-4 flex items-center gap-2">
                          <span>üì•</span>
                          Rendus des √©tudiants
                        </h4>
                        <div className="space-y-3 max-h-48 overflow-y-auto pr-2">
                          {project.submissions.map((sub, index) => (
                            <div key={index} className="flex justify-between items-center bg-white/80 p-4 rounded-xl border border-purple-100 shadow-sm hover:shadow-md transition-all">
                              <div className="flex-1 min-w-0">
                                <p className="font-semibold text-gray-800 text-sm truncate">
                                  {sub.customName}
                                </p>
                                <p className="text-xs text-gray-500 mt-1">
                                  üë§ {sub.student?.name || '√âtudiant'} ‚Ä¢ 
                                  üìÖ {new Date(sub.submittedAt).toLocaleDateString('fr-FR')}
                                </p>
                              </div>
                              <a 
                                href={`http://localhost:5000/${sub.file}`} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="flex-shrink-0 ml-3 px-4 py-2 bg-gradient-to-r from-indigo-600 to-purple-600 text-white text-sm rounded-lg hover:from-indigo-700 hover:to-purple-700 transition-all shadow hover:shadow-lg"
                              >
                                üì•
                              </a>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                    {/* Boutons d'action */}
                    <div className="flex gap-4">
                      <button
                        onClick={() => openProfModal(project)}
                        className="flex-1 py-4 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-semibold rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex items-center justify-center gap-2"
                      >
                        <span>‚úèÔ∏è</span>
                        Modifier
                      </button>
                      <button
                        onClick={() => deleteProject(project._id)}
                        className="flex-1 py-4 bg-gradient-to-r from-rose-600 to-pink-600 text-white font-semibold rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:scale-105 flex items-center justify-center gap-2"
                      >
                        <span>üóëÔ∏è</span>
                        Supprimer
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* Message si aucun projet - STYLE AM√âLIOR√â */}
      {projects.length === 0 && (
        <div className="text-center py-20">
          <div className="bg-white/80 backdrop-blur-lg rounded-3xl p-16 shadow-2xl border border-white/30 max-w-2xl mx-auto">
            <div className="text-8xl mb-6">üìö</div>
            <h3 className="text-3xl font-bold text-gray-800 mb-4">Aucun projet disponible</h3>
            <p className="text-gray-600 text-lg mb-8 max-w-md mx-auto">
              {isProf 
                ? "Commencez par cr√©er votre premier projet pour organiser votre enseignement."
                : "Aucun projet n'a √©t√© assign√© pour le moment."}
            </p>
            {isProf && (
              <button
                onClick={() => openProfModal()}
                className="px-10 py-4 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-bold text-lg rounded-xl shadow-lg hover:shadow-xl transition-all transform hover:scale-105"
              >
                Cr√©er mon premier projet
              </button>
            )}
          </div>
        </div>
      )}

      {/* MODAL √âTUDIANT - STYLE AM√âLIOR√â */}
      <Modal
        isOpen={studentModal}
        onRequestClose={() => {
          setStudentModal(false);
          setSelectedProject(null);
        }}
        className="bg-white p-12 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none border border-white/30"
        overlayClassName="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50"
      >
        <div className="space-y-8">
          <div className="text-center">
            <div className="text-5xl mb-4">üì§</div>
            <h2 className="text-4xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
              D√©poser mon rendu
            </h2>
          </div>
          
          {selectedProject && (
            <div className="bg-gradient-to-r from-indigo-50 to-purple-50 p-5 rounded-2xl border-2 border-indigo-200">
              <p className="font-semibold text-indigo-800 text-lg">üéØ {selectedProject.name}</p>
              <p className="text-indigo-600 text-sm mt-1">
                üìÖ Deadline: {new Date(selectedProject.deadline).toLocaleDateString('fr-FR')}
              </p>
            </div>
          )}
          
          <div>
            <label className="block text-lg font-semibold text-gray-800 mb-3">
              ‚úèÔ∏è Nom du fichier
            </label>
            <input
              type="text"
              placeholder="Ex: TP1 - Votre Nom.pdf"
              value={fileName}
              onChange={(e) => setFileName(e.target.value)}
              className="w-full px-6 py-4 border-2 border-gray-200 rounded-xl text-lg focus:border-indigo-500 outline-none transition-all bg-white/50 focus:bg-white"
            />
          </div>
          
          <div>
            <label className="block text-lg font-semibold text-gray-800 mb-3">
              üìé Fichier √† d√©poser
            </label>
            <div className="border-2 border-dashed border-gray-300 rounded-2xl p-8 text-center transition-all hover:border-indigo-400 hover:bg-indigo-50/30">
              <input
                type="file"
                accept=".pdf,.docx,.zip,.jpg,.jpeg,.png"
                onChange={(e) => setFile(e.target.files?.[0] || null)}
                className="absolute opacity-0 cursor-pointer w-full h-full"
              />
              <div className="text-4xl mb-3">üìé</div>
              <p className="text-gray-600 font-medium">
                {file ? `‚úÖ ${file.name}` : "Cliquez pour choisir un fichier"}
              </p>
              <p className="text-gray-500 text-sm mt-2">
                PDF, Word, ZIP, Images (max 15Mo)
              </p>
            </div>
          </div>
          
          <div className="flex gap-4 pt-4">
            <button
              onClick={submitStudentFile}
              className="flex-1 py-5 bg-gradient-to-r from-emerald-500 to-green-600 text-white font-bold text-xl rounded-xl shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:scale-105 flex items-center justify-center gap-3"
            >
              <span>üöÄ</span>
              {selectedProject && mySubmission(selectedProject) ? 'Mettre √† jour' : 'D√©poser'}
            </button>
            <button
              onClick={() => {
                setStudentModal(false);
                setSelectedProject(null);
              }}
              className="flex-1 py-5 bg-gradient-to-r from-gray-500 to-gray-600 text-white font-bold text-xl rounded-xl hover:shadow-lg transition-all duration-300"
            >
              Annuler
            </button>
          </div>
        </div>
      </Modal>

      {/* MODAL PROF - STYLE AM√âLIOR√â */}
      <Modal
        isOpen={profModal}
        onRequestClose={() => {
          setProfModal(false);
          setEditProject(null);
        }}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none border border-white/30"
        overlayClassName="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50"
        closeTimeoutMS={200}
      >
        <ProjectForm
          currentProject={editProject}
          closeModal={() => {
            setProfModal(false);
            setEditProject(null);
          }}
          refreshProjects={refreshProjects}
        />
      </Modal>
    </div>
  );
};

export default ProjectList;
