// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous ses fichiers associés ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du projet');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Voulez-vous vraiment supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du rendu');
    }
  };

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  const getMySubmission = (project) => 
    project.submissions?.find(s => s.student._id === user._id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-blue-50 to-indigo-50 p-8">
      {/* Bouton Ajouter – Prof */}
      {isProf && (
        <div className="text-center mb-16">
          <button
            onClick={() => openModal()}
            className="px-10 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 text-white text-2xl font-bold rounded-full shadow-2xl hover:shadow-purple-600 transition-all duration-300 transform hover:scale-105"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      {/* Grille des projets */}
      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white/90 backdrop-blur-sm rounded-3xl shadow-2xl p-8 border border-white/50 hover:shadow-purple-200 transition-all duration-300"
          >
            <h3 className="text-3xl font-bold text-indigo-900 mb-3">{project.name}</h3>
            <p className="text-gray-600 text-lg mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-gray-700 font-medium mb-8">
              Deadline : <span className="text-indigo-600 font-bold">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-8 px-8 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold text-base rounded-full shadow-lg hover:shadow-blue-700 transition-all"
              >
                Télécharger le sujet
              </a>
            )}

            {/* SECTION ÉTUDIANT – EXACTEMENT COMME TON IMAGE */}
            {!isProf && (
              <div className="mt-8 p-6 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-3xl border-2 border-indigo-200 shadow-inner">
                {hasSubmitted(project) ? (
                  <div className="text-center space-y-6">
                    <p className="text-green-700 font-bold text-2xl tracking-wide">Rendu déposé !</p>

                    {getMySubmission(project)?.file && (
                      <a
                        href={`http://localhost:5000/${getMySubmission(project).file}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="block text-indigo-600 underline text-sm font-medium hover:text-indigo-800"
                      >
                        Voir mon fichier
                      </a>
                    )}

                    {/* Deux boutons sur la même ligne – petits et magnifiques */}
                    <div className="flex justify-center gap-5">
                      <label className="cursor-pointer">
                        <input
                          type="file"
                          accept=".pdf,.zip,.docx"
                          onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
                          className="hidden"
                        />
                        <span className="inline-block px-7 py-3.5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-sm rounded-full shadow-lg hover:shadow-xl transition-all">
                          Modifier
                        </span>
                      </label>

                      <button
                        onClick={() => handleDeleteSubmission(project._id)}
                        className="px-7 py-3.5 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold text-sm rounded-full shadow-lg hover:shadow-xl transition-all"
                      >
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="text-center">
                    <label className="cursor-pointer">
                      <input
                        type="file"
                        accept=".pdf,.zip,.docx"
                        onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
                        className="hidden"
                      />
                      <span className="inline-block px-10 py-5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-lg rounded-full shadow-2xl hover:shadow-purple-700 transition-all transform hover:scale-105">
                        Déposer mon travail
                      </span>
                    </label>
                  </div>
                )}
              </div>
            )}

            {/* SECTION PROF */}
            {isProf && project.submissions?.length > 0 && (
              <div className="mt-8 bg-purple-50 p-6 rounded-3xl border-2 border-purple-200">
                <p className="font-bold text-purple-800 text-xl mb-4">
                  {project.submissions.length} rendu(s) reçu(s)
                </p>
                {project.submissions.map((s, i) => (
                  <div key={i} className="bg-white p-4 rounded-xl mb-3 shadow text-sm">
                    <strong className="text-purple-700">{s.student.name}</strong>
                    <a href={`http://localhost:5000/${s.file}`} target="_blank" className="text-blue-600 underline ml-3">
                      Voir
                    </a>
                  </div>
                ))}
              </div>
            )}

            {/* Boutons prof */}
            {isProf && (
              <div className="flex justify-center gap-6 mt-10">
                <button
                  onClick={() => openModal(project)}
                  className="px-10 py-4 bg-gradient-to-r from-blue-50 to-blue-100 hover:from-blue-100 hover:to-blue-200 text-blue-700 font-bold text-lg rounded-2xl shadow-lg hover:shadow-xl transition-all border border-blue-200"
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(project._id)}
                  className="px-10 py-4 bg-gradient-to-r from-purple-50 to-purple-100 hover:from-purple-100 hover:to-purple-200 text-purple-700 font-bold text-lg rounded-2xl shadow-lg hover:shadow-xl transition-all border border-purple-200"
                >
                  Supprimer
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-12 rounded-3xl shadow-2xl max-w-4xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
