// src/components/Dashboard.js
import { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import ProgressChart from './ProgressChart';
import ExportButtons from './ExportButtons';
import API from '../services/api';

const Dashboard = ({ user }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');

  // Chargement des projets au montage
  const fetchProjects = async () => {
    try {
      setLoading(true);
      const { data } = await API.get('/projects');
      setProjects(data || []);
    } catch (err) {
      console.error("Erreur chargement projets :", err);
      setProjects([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  // Filtre selon le rôle
  const isProf = user?.role === 'admin';

  const filteredProjects = projects.filter(p => {
    if (filter === 'all') return true;
    if (filter === 'en cours') return true; // Tous les projets sont visibles
    if (filter === 'rendu') return p.submissions?.some(s => s.student._id === user?._id);
    if (filter === 'non-rendu') return !p.submissions?.some(s => s.student._id === user?._id);
    return true;
  });

  if (loading) {
    return (
      <div className="container mx-auto p-10 text-center">
        <div className="text-2xl text-gray-600">Chargement des projets...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 min-h-screen bg-gray-50">
      {/* Titre */}
      <div className="text-center mb-10">
        <h1 className="text-5xl font-extrabold text-indigo-700">
          {isProf ? 'Espace Professeur' : `Bienvenue, ${user?.name || 'Étudiant'}`}
        </h1>
        <p className="text-xl text-gray-600 mt-3">
          {isProf 
            ? 'Gérez vos projets et suivez les rendus de vos étudiants' 
            : 'Consultez les projets assignés et déposez vos comptes-rendus'}
        </p>
      </div>

      {/* Filtre pour étudiants */}
      {!isProf && (
        <div className="mb-8 text-center">
          <select
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="p-4 text-lg border-2 border-indigo-300 rounded-xl shadow-md focus:outline-none focus:border-indigo-600"
          >
            <option value="all">Tous les projets</option>
            <option value="rendu">J'ai déjà rendu</option>
            <option value="non-rendu">À rendre</option>
          </select>
        </div>
      )}

      {/* Liste des projets */}
      <div className="max-w-7xl mx-auto">
        {filteredProjects.length === 0 ? (
          <div className="text-center py-20">
            <p className="text-2xl text-gray-500">
              {isProf 
                ? 'Aucun projet publié pour le moment. Créez-en un !' 
                : filter === 'non-rendu' 
                  ? 'Félicitations ! Tous vos projets sont rendus !' 
                  : 'Aucun projet disponible pour le moment.'}
            </p>
          </div>
        ) : (
          <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
            {filteredProjects.map(project => (
              <ProjectList
                key={project._id}
                project={project}
                user={user}
                refreshProjects={fetchProjects}
              />
            ))}
          </div>
        )}
      </div>

      {/* Graphique + Export (seulement pour le prof) */}
      {isProf && (
        <div className="mt-16 bg-white p-10 rounded-3xl shadow-2xl border">
          <h2 className="text-3xl font-bold text-center text-indigo-700 mb-8">
            Statistiques Globales
          </h2>
          <div className="grid md:grid-cols-2 gap-10">
            <ProgressChart projects={projects} />
            <ExportButtons projects={projects} />
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
