// src/components/ProjectList.js → VERSION FINALE (copie-colle intégralement)
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [editProject, setEditProject] = useState(null); // null = création, objet = modification
  const [selectedProject, setSelectedProject] = useState(null);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const isProf = user?.role === 'admin';

  const mySubmission = (project) =>
    project.submissions?.find((s) => s.student._id?.toString() === user._id.toString());

  const openSubmitModal = (project) => {
    setSelectedProject(project);
    setFileName(mySubmission(project)?.customName || '');
    setFile(null);
    setEditProject(null); // important
    setModalOpen(true);
  };

  const openEditModal = (project = null) => {
    setEditProject(project);
    setModalOpen(true);
  };

  const submitFile = async () => {
    if (!file) return alert('Veuillez sélectionner un fichier');
    if (!fileName.trim()) return alert('Veuillez donner un nom au fichier');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('Rendu déposé avec succès !');
      setModalOpen(false);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt. Réessayez.');
    }
  };

  const handleDelete = (projectId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce projet ?')) {
      API.delete(`/projects/${projectId}`)
        .then(() => {
          refreshProjects();
        })
        .catch(() => alert('Erreur lors de la suppression'));
    }
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8 pb-20">
      {/* Bouton Créer un projet (prof uniquement) */}
      {isProf && (
        <div className="text-center mb-10">
          <button
            onClick={() => openEditModal()}
            className="px-8 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow hover:bg-indigo-700 transition"
          >
            + Nouveau projet
          </button>
        </div>
      )}

      {/* Grille des projets */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition duration-300"
          >
            <div className="p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-2">{project.name}</h3>
              <p className="text-gray-600 text-sm mb-3">
                {project.description || 'Aucune description'}
              </p>
              <p className="text-sm font-medium text-gray-700">
                Deadline :{' '}
                <span className="text-indigo-600 font-bold">
                  {new Date(project.deadline).toLocaleDateString('fr-FR')}
                </span>
              </p>

              {project.pdf && (
                <a
                  href={`http://localhost:5000/${project.pdf}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="block mt-3 text-indigo-600 hover:underline text-sm"
                >
                  Sujet PDF
                </a>
              )}

              {/* Vue Étudiant */}
              {!isProf && (
                <div className="mt-5">
                  {mySubmission(project) ? (
                    <div className="bg-green-50 border border-green-300 rounded-lg p-4 text-center">
                      <p className="text-green-800 font-semibold text-sm">Rendu déposé</p>
                      <p className="text-xs text-gray-600 mt-1 truncate">
                        {mySubmission(project).customName}
                      </p>
                      <button
                        onClick={() => openSubmitModal(project)}
                        className="mt-3 text-xs text-indigo-600 underline hover:text-indigo-800"
                      >
                        Modifier
                      </button>
                    </div>
                  ) : (
                    <button
                      onClick={() => openSubmitModal(project)}
                      className="w-full py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-medium rounded-lg hover:opacity-90 transition"
                    >
                      Déposer mon rendu
                    </button>
                  )}
                </div>
              )}

              {/* Vue Professeur */}
              {isProf && (
                <div className="mt-5 border-t pt-4">
                  <p className="text-sm font-bold text-purple-700 text-center mb-3">
                    {project.submissions?.length || 0} rendu(s) reçu(s)
                  </p>

                  {project.submissions?.length > 0 && (
                    <div className="space-y-2 max-h-40 overflow-y-auto text-xs">
                      {project.submissions.map((sub, i) => (
                        <div key={i} className="bg-gray-50 p-2 rounded border">
                          <p className="font-medium truncate">{sub.customName}</p>
                          <p className="text-gray-600">{sub.student?.name || 'Anonyme'}</p>
                          <a
                            href={`http://localhost:5000/${sub.file}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="text-green-600 hover:underline text-xs"
                          >
                            Télécharger
                          </a>
                        </div>
                      ))}
                    </div>
                  )}

                  <div className="flex gap-2 mt-4">
                    <button
                      onClick={() => openEditModal(project)}
                      className="flex-1 py-2 bg-gray-600 text-white text-xs rounded hover:bg-gray-700 transition"
                    >
                      Modifier
                    </button>
                    <button
                      onClick={() => handleDelete(project._id)}
                      className="flex-1 py-2 bg-red-600 text-white text-xs rounded hover:bg-red-700 transition"
                    >
                      Supprimer
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Modal unique : dépôt étudiant OU création/modif projet */}
      <Modal
        isOpen={modalOpen}
        onRequestClose={() => setModalOpen(false)}
        className="bg-white p-8 rounded-xl shadow-2xl max-w-md mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
      >
        {/* Mode dépôt étudiant */}
        {editProject === null && selectedProject && (
          <div>
            <h2 className="text-2xl font-bold mb-6 text-center text-indigo-800">
              {mySubmission(selectedProject) ? 'Modifier mon rendu' : 'Déposer mon rendu'}
            </h2>
            <input
              type="text"
              placeholder="Ex: TP1 - Nom Prénom.pdf"
              value={fileName}
              onChange={(e) => setFileName(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg mb-4 focus:outline-none focus:border-indigo-500"
            />
            <input
              type="file"
              accept=".pdf,.docx,.zip,.jpg,.jpeg,.png"
              onChange={(e) => setFile(e.target.files[0])}
              className="w-full mb-6 file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:bg-indigo-600 file:text-white"
            />
            <div className="flex gap-4">
              <button
                onClick={submitFile}
                className="flex-1 py-3 bg-green-600 text-white rounded-lg font-medium hover:bg-green-700 transition"
              >
                {mySubmission(selectedProject) ? 'Mettre à jour' : 'Déposer'}
              </button>
              <button
                onClick={() => setModalOpen(false)}
                className="flex-1 py-3 bg-gray-500 text-white rounded-lg font-medium hover:bg-gray-600 transition"
              >
                Annuler
              </button>
            </div>
          </div>
        )}

        {/* Mode création/modification projet (prof) */}
        {editProject !== null && (
          <ProjectForm
            currentProject={editProject}
            closeModal={() => {
              setModalOpen(false);
              setEditProject(null);
            }}
            refreshProjects={refreshProjects}
          />
        )}
      </Modal>
    </div>
  );
};

export default ProjectList;
