import { useState, useEffect } from 'react';
import API from '../services/api';
import socket from '../socket';

const ProjectForm = ({ closeModal, currentProject, setProjects, projects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [pdf, setPdf] = useState(null);

  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name);
      setDescription(currentProject.description);
      setDeadline(new Date(currentProject.deadline).toISOString().split('T')[0]);
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    if (pdf) formData.append('pdf', pdf);

    try {
      let updatedProject;
      if (currentProject) {
        const { data } = await API.put(`/projects/${currentProject._id}`, formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        updatedProject = data;
        setProjects(projects.map(p => p._id === currentProject._id ? updatedProject : p));
      } else {
        const { data } = await API.post('/projects', formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        updatedProject = data;
        setProjects([...projects, updatedProject]);
      }
      socket.emit('updateProject', { projectId: updatedProject._id, ...updatedProject });
      closeModal();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Nom" className="mb-4 p-2 border w-full" />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" className="mb-4 p-2 border w-full" />
      <input type="date" value={deadline} onChange={(e) => setDeadline(e.target.value)} className="mb-4 p-2 border w-full" />
      <input type="file" onChange={(e) => setPdf(e.target.files[0])} className="mb-4" />
      <button type="submit" className="bg-green-500 text-white p-2">Sauvegarder</button>
    </form>
  );
};

export default ProjectForm;
