// src/components/ProjectForm.js
import { useState, useEffect } from 'react';
import API from '../services/api';

const ProjectForm = ({ currentProject, closeModal, refreshProjects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [pdf, setPdf] = useState(null);

  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name || '');
      setDescription(currentProject.description || '');
      setDeadline(currentProject.deadline ? new Date(currentProject.deadline).toISOString().split('T')[0] : '');
    } else {
      setName('');
      setDescription('');
      setDeadline('');
      setPdf(null);
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    if (pdf) formData.append('pdf', pdf);

    try {
      if (currentProject) {
        await API.put(`/projects/${currentProject._id}`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      } else {
        await API.post('/projects', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      }
      refreshProjects();
      closeModal();
    } catch (err) {
      console.error("Erreur sauvegarde :", err);
      alert("Erreur lors de la sauvegarde. Vérifie que le backend est allumé.");
    }
  };

  return (
    <div className="p-8">
      <h2 className="text-4xl font-bold text-center text-indigo-800 mb-10">
        {currentProject ? 'Modifier le projet' : 'Nouveau projet'}
      </h2>

      <form onSubmit={handleSubmit} className="space-y-8">
        <input
          type="text"
          placeholder="Nom du projet"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className="w-full p-5 text-lg border-2 border-gray-200 rounded-2xl focus:border-indigo-400 outline-none transition"
        />

        <textarea
          placeholder="Description (facultatif)"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows="5"
          className="w-full p-5 text-lg border-2 border-gray-200 rounded-2xl focus:border-indigo-400 outline-none resize-none transition"
        />

        <input
          type="date"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
          required
          className="w-full p-5 text-lg border-2 border-gray-200 rounded-2xl focus:border-indigo-400 outline-none transition"
        />

        <div>
          <label className="block text-lg font-medium text-gray-700 mb-3">PDF du sujet (facultatif)</label>
          <input
            type="file"
            accept=".pdf"
            onChange={(e) => setPdf(e.target.files[0] || null)}
            className="block w-full text-sm text-gray-600 file:mr-4 file:py-3 file:px-8 file:rounded-full file:bg-indigo-600 file:text-white hover:file:bg-indigo-700 cursor-pointer"
          />
          {currentProject?.pdf && !pdf && (
            <p className="text-sm text-gray-500 mt-2">
              Fichier actuel : {currentProject.pdf.split('/').pop()}
            </p>
          )}
        </div>

        {/* BOUTONS PARFAITS – MÊME STYLE QUE MODIFIER/SUPPRIMER */}
        <div className="flex justify-center gap-8 pt-8">
          <button
            type="button"
            onClick={closeModal}
            className="px-12 py-5 bg-gradient-to-r from-gray-100 to-gray-200 hover:from-gray-200 hover:to-gray-300 text-gray-700 font-bold text-xl rounded-2xl shadow-lg hover:shadow-xl transition-all border border-gray-300"
          >
            Annuler
          </button>

          <button
            type="submit"
            className="px-12 py-5 bg-gradient-to-r from-emerald-500 to-teal-600 hover:from-emerald-600 hover:to-teal-700 text-white font-bold text-xl rounded-2xl shadow-lg hover:shadow-2xl transition-all"
          >
            {currentProject ? 'Sauvegarder' : 'Créer le projet'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProjectForm;
