import { useState } from 'react';
import ProjectList from './ProjectList';
import ProgressChart from './ProgressChart';
import ExportButtons from './ExportButtons';

const Dashboard = ({ projects, setProjects, user }) => {
  const [filter, setFilter] = useState('all');

  const filteredProjects = projects.filter(p => filter === 'all' || p.status === filter);

  return (
    <div>
      <select onChange={(e) => setFilter(e.target.value)} className="mb-4 p-2 border">
        <option value="all">Tous</option>
        <option value="en cours">En cours</option>
        <option value="fini">Fini</option>
      </select>
      <ProjectList projects={filteredProjects} setProjects={setProjects} user={user} />
      <ProgressChart projects={projects} />
      <ExportButtons projects={projects} />
    </div>
  );
};

export default Dashboard;
