// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous les rendus ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur suppression projet');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      alert('Erreur dépôt');
    }
  };

  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur suppression rendu');
    }
  };

  const hasSubmitted = (project) =>
    project.submissions?.some(s => s.student._id === user._id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-indigo-50 p-8">
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="px-10 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-lg rounded-full shadow-xl hover:shadow-2xl transition"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        {projects.map((project) => (
          <div key={project._id} className="bg-white rounded-3xl shadow-xl p-8 border border-gray-100 hover:shadow-purple-300 transition">
            <h3 className="text-2xl font-bold text-indigo-900 mb-3">{project.name}</h3>
            <p className="text-gray-600 mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-sm font-medium text-gray-700 mb-6">
              Deadline : <span className="text-indigo-600 font-bold">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-6 px-8 py-3.5 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold text-sm rounded-full shadow-lg"
              >
                Télécharger le sujet
              </a>
            )}

            {/* ÉTUDIANT */}
            
{!isProf && (
  <div className="mt-6 p-6 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-3xl border-2 border-indigo-200">
    {hasSubmitted(project) ? (
      <div className="text-center space-y-5">
        <p className="text-green-700 font-bold text-xl">Rendu déposé !</p>
        
        <div className="flex justify-center gap-4">
          {/* BOUTON MODIFIER – CORRIGÉ */}
          <label className="cursor-pointer">
            <input
              type="file"
              accept=".pdf,.zip,.docx"
              className="hidden"
              onChange={(e) => {
                if (e.target.files && e.target.files[0]) {
                  handleSubmitFile(project._id, e.target.files[0]);
                  e.target.value = ''; // Réinitialise l'input
                }
              }}
            />
            <span className="px-7 py-3.5 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-sm rounded-full shadow-lg hover:shadow-xl transition">
              Modifier
            </span>
          </label>

          {/* BOUTON SUPPRIMER */}
          <button
            onClick={() => handleDeleteSubmission(project._id)}
            className="px-7 py-3.5 bg-gradient-to-r from-red-500 to-pink-600 text-white font-bold text-sm rounded-full shadow-lg hover:shadow-xl transition"
          >
            Supprimer
          </button>
        </div>
      </div>
    ) : (
      <div className="text-center">
        {/* BOUTON DÉPOSER – CORRIGÉ À 100% */}
        <label className="cursor-pointer inline-block">
          <input
            type="file"
            accept=".pdf,.zip,.docx"
            className="hidden"
            onChange={(e) => {
              if (e.target.files && e.target.files[0]) {
                handleSubmitFile(project._id, e.target.files[0]);
                e.target.value = ''; // Très important !
              }
            }}
          />
          <span className="inline-block px-12 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-lg rounded-full shadow-2xl hover:shadow-purple-800 transition transform hover:scale-105">
            Déposer mon travail
          </span>
        </label>
      </div>
    )}
  </div>
)}

            {/* PROF – Boutons identiques */}
            {isProf && (
              <div className="mt-8 text-center">
                <div className="flex justify-center gap-5">
                  <button onClick={() => openModal(project)} className="px-8 py-4 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-sm rounded-full shadow-lg">
                    Modifier
                  </button>
                  <button onClick={() => handleDelete(project._id)} className="px-8 py-4 bg-gradient-to-r from-red-500 to-pink-600 text-white font-bold text-sm rounded-full shadow-lg">
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* MODAL PETIT, CENTRÉ, PARFAIT */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-8 rounded-3xl shadow-2xl w-full max-w-md mx-auto my-8 outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
