// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous les rendus ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du projet');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      console.error(err);
      alert('Erreur lors du dépôt du fichier');
    }
  };

  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du rendu');
    }
  };

  // Récupère le rendu de l'étudiant connecté
  const getMySubmission = (project) => 
    project.submissions?.find(s => s.student._id === user._id);

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-indigo-50 to-blue-50 p-8">
      {/* Bouton Ajouter pour le prof */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="px-12 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-xl rounded-full shadow-2xl hover:shadow-purple-500 transition transform hover:scale-105"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white rounded-3xl shadow-2xl p-8 border border-gray-100 hover:shadow-purple-300 transition-all duration-300 transform hover:-translate-y-2"
          >
            <h3 className="text-3xl font-bold text-indigo-900 mb-3">{project.name}</h3>
            <p className="text-gray-600 text-lg mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-gray-700 font-medium mb-8">
              Deadline : <span className="text-indigo-600 font-bold text-xl">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-8 px-10 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition"
              >
                Télécharger le sujet
              </a>
            )}

            {/* SECTION ÉTUDIANT */}
            {!isProf && (
              <div className="mt-8 p-8 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-3xl border-2 border-indigo-200">
                {hasSubmitted(project) ? (
                  <div className="text-center space-y-6">
                    <p className="text-green-700 font-bold text-2xl">Rendu déposé !</p>

                    {/* AFFICHAGE DU FICHIER DÉPOSÉ */}
                    {getMySubmission(project)?.file && (
                      <div className="bg-white p-5 rounded-2xl shadow-md border border-green-200">
                        <p className="text-sm text-gray-600 mb-3">Votre compte-rendu :</p>
                        <a
                          href={`http://localhost:5000/${getMySubmission(project).file}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-block px-8 py-4 bg-gradient-to-r from-green-600 to-emerald-700 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition"
                        >
                          Voir mon fichier
                        </a>
                      </div>
                    )}

                    <div className="flex justify-center gap-6">
                      <label className="cursor-pointer">
                        <input
                          type="file"
                          accept=".pdf,.zip,.docx"
                          className="hidden"
                          onChange={(e) => {
                            const file = e.target.files?.[0];
                            if (file) {
                              handleSubmitFile(project._id, file);
                              e.target.value = '';
                            }
                          }}
                        />
                        <span className="px-8 py-4 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition">
                          Modifier
                        </span>
                      </label>

                      <button
                        onClick={() => handleDeleteSubmission(project._id)}
                        className="px-8 py-4 bg-gradient-to-r from-red-500 to-pink-600 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition"
                      >
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="text-center">
                    <label className="cursor-pointer">
                      <input
                        type="file"
                        accept=".pdf,.zip,.docx"
                        className="hidden"
                        onChange={(e) => {
                          const file = e.target.files?.[0];
                          if (file) {
                            handleSubmitFile(project._id, file);
                            e.target.value = '';
                          }
                        }}
                      />
                      <span className="inline-block px-12 py-6 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-xl rounded-full shadow-2xl hover:shadow-purple-600 transition transform hover:scale-110">
                        Déposer mon travail
                      </span>
                    </label>
                  </div>
                )}
              </div>
            )}

            {/* SECTION PROF */}
            {isProf && (
              <div className="mt-10 text-center">
                <p className="text-purple-700 font-bold text-lg mb-6">
                  {project.submissions?.length || 0} rendu(s) reçu(s)
                </p>
                <div className="flex justify-center gap-6">
                  <button
                    onClick={() => openModal(project)}
                    className="px-10 py-4 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleDelete(project._id)}
                    className="px-10 py-4 bg-gradient-to-r from-red-500 to-pink-600 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition"
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* MODAL PARFAIT : PETIT, CENTRÉ, ÉLÉGANT */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
        closeTimeoutMS={200}
      >
        <ProjectForm
          currentProject={currentProject}
          closeModal={closeModal}
          refreshProjects={refreshProjects}
        />
      </Modal>
    </div>
  );
};

export default ProjectList;
