import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import API from '../services/api';

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('etudiant');
  const history = useHistory();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post('/users/register', { name, email, password, role });
      history.push('/login');
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md">
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" className="mb-4 p-2 border" />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" className="mb-4 p-2 border" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" className="mb-4 p-2 border" />
        <select value={role} onChange={(e) => setRole(e.target.value)} className="mb-4 p-2 border">
          <option value="etudiant">Ã‰tudiant</option>
          <option value="admin">Admin</option>
        </select>
        <button type="submit" className="bg-green-500 text-white p-2">Register</button>
      </form>
    </div>
  );
};

export default Register;
