// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [editProject, setEditProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const isProf = user?.role === 'admin';

  const mySubmission = (project) => project.submissions?.find(s => 
    s.student._id?.toString() === user._id.toString()
  );

  const openSubmitModal = (project) => {
    setSelectedProject(project);
    setFileName(mySubmission(project)?.customName || '');
    setFile(null);
    setModalOpen(true);
  };

  const submitFile = async () => {
    if (!file) return alert('Choisissez un fichier');
    if (!fileName.trim()) return alert('Donnez un nom au fichier');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('Déposé avec succès !');
      setModalOpen(false);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8 pb-20">
      {/* Bouton Créer (prof seulement) */}
      {isProf && (
        <div className="text-center mb-10">
          <button
            onClick={() => { setEditProject(null); setModalOpen(true); }}
            className="px-8 py-3 bg-indigo-600 text-white font-semibold rounded-lg shadow hover:bg-indigo-700 transition"
          >
            + Nouveau projet
          </button>
        </div>
      )}

      {/* Grille de projets */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map(project => (
          <div key={project._id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition">
            <div className="p-6">
              <h3 className="text-xl font-bold text-gray-800 mb-2">{project.name}</h3>
              <p className="text-gray-600 text-sm mb-3">{project.description || 'Pas de description'}</p>
              <p className="text-sm font-medium text-gray-700">
                Deadline : <span className="text-indigo-600 font-bold">
                  {new Date(project.deadline).toLocaleDateString('fr-FR')}
                </span>
              </p>

              {project.pdf && (
                <a href={`http://localhost:5000/${project.pdf}`} target="_blank" rel="noopener noreferrer"
                  className="block mt-3 text-indigo-600 hover:underline text-sm">
                  Sujet PDF
                </a>
              )}

              {/* Étudiant */}
              {!isProf && (
                <div className="mt-5">
                  {mySubmission(project) ? (
                    <div className="bg-green-50 border border-green-300 rounded-lg p-4 text-center">
                      <p className="text-green-800 font-semibold text-sm">Rendu déposé</p>
                      <p className="text-xs text-gray-600 mt-1">{mySubmission(project).customName}</p>
                      <button onClick={() => openSubmitModal(project)}
                        className="mt-3 text-xs text-indigo-600 underline">
                        Modifier
                      </button>
                    </div>
                  ) : (
                    <button onClick={() => openSubmitModal(project)}
                      className="w-full py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-medium rounded-lg hover:opacity-90 transition">
                      Déposer mon rendu
                    </button>
                  )}
                </div>
              )}

              {/* Professeur */}
              {isProf && (
                <div className="mt-5 border-t pt-4">
                  <p className="text-sm font-bold text-purple-700 text-center mb-3">
                    {project.submissions?.length || 0} rendu(s)
                  </p>
                  {project.submissions?.length > 0 && (
                    <div className="space-y-2 max-h-40 overflow-y-auto text-xs">
                      {project.submissions.map((sub, i) => (
                        <div key={i} className="bg-gray-50 p-2 rounded border">
                          <p className="font-medium truncate">{sub.customName}</p>
                          <p className="text-gray-600">{sub.student?.name || 'Inconnu'}</p>
                          <a href={`http://localhost:5000/${sub.file}`} target="_blank"
                            className="text-green-600 hover:underline text-xs">
                            Télécharger
                          </a>
                        </div>
                      ))}
                    </div>
                  )}
                  <div className="flex gap-2 mt-4">
                    <button onClick={() => { setEditProject(project); setModalOpen(true); }}
                      className="flex-1 py-2 bg-gray-600 text-white text-xs rounded hover:bg-gray-700">
                      Modifier
                    </button>
                    <button onClick={() => confirm('Supprimer ?') && API.delete(`/projects/${project._id}`).then(refreshProjects)}
                      className="flex-1 py-2 bg-red-600 text-white text-xs rounded hover:bg-red-700">
                      Supprimer
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Modal dépôt ou création */}
      <Modal isOpen={modalOpen} onRequestClose={() => setModalOpen(false)}
        className="bg-white p-8 rounded-xl shadow-2xl max-w-md mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        {editProject === null ? (
          /* Formulaire dépôt étudiant */
          <div>
            <h2 className="text-2xl font-bold mb-6 text-center">Déposer mon rendu</h2>
            <input type="text" placeholder="Nom du fichier (ex: TP1 - Nom.pdf)"
              value={fileName} onChange={e => setFileName(e.target.value)}
              className="w-full px-4 py-3 border rounded-lg mb-4" />
            <input type="file" accept=".pdf,.docx,.zip" onChange={e => setFile(e.target.files[0])}
              className="w-full mb-6" />
            <div className="flex gap-4">
              <button onClick={submitFile}
                className="flex-1 py-3 bg-green-600 text-white rounded-lg font-medium hover:bg-green-700">
                Déposer
              </button>
              <button onClick={() => setModalOpen(false)}
                className="flex-1 py-3 bg-gray-500 text-white rounded-lg font-medium">
                Annuler
              </button>
            </div>
          </div>
        ) : (
          /* Formulaire création/modif projet (prof) */
          <ProjectForm
            currentProject={editProject}
            closeModal={() => { setModalOpen(false); setEditProject(null); }}
            refreshProjects={refreshProjects}
          />
        )}
      </Modal>
    </div>
  );
};

export default ProjectList;
