// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous ses rendus ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur : impossible de supprimer le projet');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  const getMySubmission = (project) => 
    project.submissions?.find(s => s.student._id === user._id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-blue-50 to-indigo-50 p-8">
      {/* Bouton Ajouter – même style que l'étudiant */}
      {isProf && (
        <div className="text-center mb-16">
          <button
            onClick={() => openModal()}
            className="px-10 py-5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-lg rounded-full shadow-xl hover:shadow-2xl transition-all transform hover:scale-105"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white/95 backdrop-blur-sm rounded-3xl shadow-2xl p-8 border border-white/60 hover:shadow-purple-300 transition-all duration-300"
          >
            <h3 className="text-3xl font-bold text-indigo-900 mb-3">{project.name}</h3>
            <p className="text-gray-600 text-lg mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-gray-700 font-medium mb-8">
              Deadline : <span className="text-indigo-600 font-bold">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-8 px-8 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold text-base rounded-full shadow-lg hover:shadow-blue-700 transition-all"
              >
                Télécharger le sujet
              </a>
            )}

            {/* SECTION ÉTUDIANT */}
            {!isProf && (
              <div className="mt-8 p-6 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-3xl border-2 border-indigo-200">
                {hasSubmitted(project) ? (
                  <div className="text-center space-y-6">
                    <p className="text-green-700 font-bold text-2xl">Rendu déposé !</p>
                    {getMySubmission(project)?.file && (
                      <a href={`http://localhost:5000/${getMySubmission(project).file}`} target="_blank" className="block text-indigo-600 underline text-sm">
                        Voir mon fichier
                      </a>
                    )}
                    <div className="flex justify-center gap-5">
                      <label className="cursor-pointer">
                        <input type="file" accept=".pdf,.zip,.docx" onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])} className="hidden" />
                        <span className="px-7 py-3.5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-sm rounded-full shadow-lg">
                          Modifier
                        </span>
                      </label>
                      <button onClick={() => handleDeleteSubmission(project._id)} className="px-7 py-3.5 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold text-sm rounded-full shadow-lg">
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="text-center">
                    <label className="cursor-pointer">
                      <input type="file" accept=".pdf,.zip,.docx" onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])} className="hidden" />
                      <span className="inline-block px-10 py-5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-lg rounded-full shadow-2xl transition-all transform hover:scale-105">
                        Déposer mon travail
                      </span>
                    </label>
                  </div>
                )}
              </div>
            )}

            {/* SECTION PROF – BOUTONS IDENTIQUES À L'ÉTUDIANT */}
            {isProf && (
              <div className="mt-8 text-center space-y-6">
                {project.submissions?.length > 0 && (
                  <p className="text-purple-700 font-bold text-lg">
                    {project.submissions.length} rendu(s) reçu(s)
                  </p>
                )}

                <div className="flex justify-center gap-5">
                  <button
                    onClick={() => openModal(project)}
                    className="px-8 py-4 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-base rounded-full shadow-lg hover:shadow-xl transition-all"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleDelete(project._id)}
                    className="px-8 py-4 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold text-base rounded-full shadow-lg hover:shadow-xl transition-all"
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* MODAL PLUS PETIT ET ÉLÉGANT */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-2xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
