// src/components/ProjectList.js → VERSION SIMPLE, PROPRE, PARFAITE (comme le prof !)
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import TaskList from './TaskList';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [profModal, setProfModal] = useState(false);
  const [studentModal, setStudentModal] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [fileName, setFileName] = useState('');
  const [file, setFile] = useState(null);

  const isProf = user?.role === 'admin';

  // === PROF : ajouter/modifier projet ===
  const openProfModal = (project = null) => {
    setCurrentProject(project);
    setProfModal(true);
  };

  // === ÉTUDIANT : déposer compte-rendu ===
  const openStudentModal = (project) => {
    setSelectedProject(project);
    const submission = project.submissions?.find(
      s => (s.student._id || s.student)?.toString() === user._id
    );
    setFileName(submission?.customName || '');
    setFile(null);
    setStudentModal(true);
  };

  const submitRendu = async () => {
    if (!file) return alert('Choisis un fichier !');
    if (!fileName.trim()) return alert('Donne un nom à ton rendu !');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('Compte-rendu déposé avec succès !');
      setStudentModal(false);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  const deleteRendu = async (projectId) => {
    if (!confirm('Supprimer ton compte-rendu ?')) return;
    await API.delete(`/projects/${projectId}/submission`);
    refreshProjects();
  };

  const hasSubmitted = (project) => {
    return project.submissions?.some(
      s => (s.student._id || s.student)?.toString() === user._id
    );
  };

  const mySubmission = (project) => {
    return project.submissions?.find(
      s => (s.student._id || s.student)?.toString() === user._id
    );
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-indigo-50 to-blue-50 p-8">

      {/* Bouton Ajouter Projet (Prof) */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openProfModal()}
            className="px-12 py-6 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-2xl rounded-full shadow-2xl transform hover:scale-105 transition"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map((project) => (
          <div key={project._id} className="bg-white rounded-3xl shadow-2xl p-10 border border-gray-100 hover:shadow-purple-400 transition-all duration-300">

            <h3 className="text-3xl font-bold text-indigo-900 mb-4">{project.name}</h3>
            <p className="text-gray-600 text-lg mb-6">{project.description || 'Pas de description'}</p>
            <p className="text-gray-700 font-semibold mb-8">
              Deadline : <span className="text-indigo-600 font-bold text-xl">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {project.pdf && (
              <a href={`http://localhost:5000/${project.pdf}`} target="_blank" rel="noopener noreferrer"
                className="inline-block mb-8 px-10 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition">
                Télécharger le sujet
              </a>
            )}

            {/* === ÉTUDIANT === */}
            {!isProf && (
              <div className="mt-8">
                {hasSubmitted(project) ? (
                  <div className="text-center p-6 bg-green-50 rounded-2xl border-2 border-green-300">
                    <p className="text-green-800 font-bold text-xl mb-4">Rendu déposé !</p>
                    <p className="font-semibold text-indigo-900 mb-3">
                      {mySubmission(project).customName}
                    </p>
                    <a href={`http://localhost:5000/${mySubmission(project).file}`} target="_blank" rel="noopener noreferrer"
                      className="inline-block px-8 py-3 bg-green-600 text-white rounded-full">
                      Voir mon fichier
                    </a>
                    <div className="flex gap-4 mt-6 justify-center">
                      <button onClick={() => openStudentModal(project)}
                        className="px-8 py-3 bg-indigo-600 text-white rounded-full font-bold hover:bg-indigo-700 transition">
                        Modifier
                      </button>
                      <button onClick={() => deleteRendu(project._id)}
                        className="px-8 py-3 bg-red-600 text-white rounded-full font-bold hover:bg-red-700 transition">
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <button
                    onClick={() => openStudentModal(project)}
                    className="w-full py-6 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-2xl rounded-2xl shadow-xl transform hover:scale-105 transition"
                  >
                    Déposer mon compte-rendu
                  </button>
                )}

                {/* Tâches de l'étudiant */}
                <div className=" w-full mt-10">
                  <TaskList projectId={project._id} user={user} />
                </div>
              </div>
            )}

            {/* === PROF === */}
            {isProf && (
              <div className="mt-10 text-center">
                <p className="text-purple-700 font-bold text-xl mb-6">
                  {project.submissions?.length || 0} rendu(s)
                </p>
                <div className="flex justify-center gap-6">
                  <button onClick={() => openProfModal(project)}
                    className="px-10 py-4 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition">
                    Modifier
                  </button>
                  <button onClick={() => API.delete(`/projects/${project._id}`).then(refreshProjects)}
                    className="px-10 py-4 bg-gradient-to-r from-red-500 to-pink-600 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition">
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal Prof */}
      <Modal isOpen={profModal} onRequestClose={() => setProfModal(false)}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
        <ProjectForm currentProject={currentProject} closeModal={() => setProfModal(false)} refreshProjects={refreshProjects} />
      </Modal>

      {/* Modal Étudiant → IDENTIQUE AU PROF EN STYLE */}
      <Modal isOpen={studentModal} onRequestClose={() => setStudentModal(false)}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
        <div className="text-center">
          <h2 className="text-3xl font-bold text-indigo-900 mb-8">
            {hasSubmitted(selectedProject) ? 'Modifier mon rendu' : 'Déposer mon compte-rendu'}
          </h2>

          <input
            type="text"
            placeholder="Nom du fichier (ex: TP1 - Jean Dupont.pdf)"
            value={fileName}
            onChange={(e) => setFileName(e.target.value)}
            className="w-full px-6 py-4 border-2 border-indigo-300 rounded-xl text-lg mb-6 focus:outline-none focus:border-indigo-600"
          />

          <input
            type="file"
            accept=".pdf,.docx,.zip"
            onChange={(e) => setFile(e.target.files[0])}
            className="block w-full text-lg text-gray-900 border border-gray-300 rounded-xl cursor-pointer bg-gray-50 p-4 mb-8"
          />

          <div className="flex justify-center gap-6">
            <button
              onClick={submitRendu}
              className="px-12 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-xl rounded-full shadow-2xl transition transform hover:scale-105"
            >
              {hasSubmitted(selectedProject) ? 'Mettre à jour' : 'Déposer'}
            </button>
            <button
              onClick={() => setStudentModal(false)}
              className="px-12 py-5 bg-gray-500 hover:bg-gray-600 text-white font-bold text-xl rounded-full"
            >
              Annuler
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default ProjectList;
