// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  // SUPPRIMER UN PROJET – CORRIGÉ
  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous ses rendus ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      console.error(err);
      alert('Erreur : impossible de supprimer le projet');
    }
  };

  // DÉPOSER / MODIFIER FICHIER ÉTUDIANT
  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  // SUPPRIMER SON RENDU
  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  const getMySubmission = (project) => 
    project.submissions?.find(s => s.student._id === user._id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-blue-50 to-indigo-50 p-6">
      {/* Bouton Ajouter – Prof */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="px-10 py-5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-lg rounded-full shadow-xl transition-all"
          >
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white rounded-3xl shadow-xl p-8 border border-gray-100 hover:shadow-purple-200 transition-all"
          >
            <h3 className="text-2xl font-bold text-indigo-900 mb-2">{project.name}</h3>
            <p className="text-gray-600 mb-3">{project.description || 'Pas de description'}</p>
            <p className="text-sm text-gray-700 mb-6">
              Deadline : <span className="font-bold text-indigo-600">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-6 px-8 py-3.5 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold text-sm rounded-full shadow-md hover:shadow-lg transition"
              >
                Télécharger le sujet
              </a>
            )}

            {/* ÉTUDIANT */}
            {!isProf && (
              <div className="mt-6 p-5 bg-gradient-to-r from-indigo-50 to-purple-50 rounded-3xl border-2 border-indigo-200">
                {hasSubmitted(project) ? (
                  <div className="text-center space-y-4">
                    <p className="text-green-700 font-bold text-xl">Rendu déposé !</p>
                    <div className="flex justify-center gap-4">
                      <label className="cursor-pointer">
                        <input type="file" accept=".pdf,.zip,.docx" onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])} className="hidden" />
                        <span className="px-7 py-3 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-sm rounded-full shadow-lg">
                          Modifier
                        </span>
                      </label>
                      <button onClick={() => handleDeleteSubmission(project._id)} className="px-7 py-3 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold text-sm rounded-full shadow-lg">
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="text-center">
                    <label className="cursor-pointer">
                      <input type="file" accept=".pdf,.zip,.docx" onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])} className="hidden" />
                      <span className="px-10 py-4 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-base rounded-full shadow-xl transition">
                        Déposer mon travail
                      </span>
                    </label>
                  </div>
                )}
              </div>
            )}

            {/* PROF – Boutons identiques à l'étudiant */}
            {isProf && (
              <div className="mt-8 text-center">
                <div className="flex justify-center gap-5">
                  <button
                    onClick={() => openModal(project)}
                    className="px-8 py-3.5 bg-gradient-to-r from-indigo-500 to-purple-600 hover:from-indigo-600 hover:to-purple-700 text-white font-bold text-sm rounded-full shadow-lg transition"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleDelete(project._id)}
                    className="px-8 py-3.5 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold text-sm rounded-full shadow-lg transition"
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* MODAL TRÈS PETIT ET ÉLÉGANT */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-8 rounded-3xl shadow-2xl max-w-md mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50"
      >
        <div className="max-h-screen overflow-y-auto">
          <ProjectForm
            currentProject={currentProject}
            closeModal={closeModal}
            refreshProjects={refreshProjects}
          />
        </div>
      </Modal>
    </div>
  );
};

export default ProjectList;
