// src/components/ProjectForm.js
import { useState, useEffect } from 'react';
import API from '../services/api';
import socket from '../socket';

const ProjectForm = ({ closeModal, currentProject, setProjects, projects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [status, setStatus] = useState('en cours');
  const [pdf, setPdf] = useState(null);

  // Pré-remplir le formulaire si on modifie un projet
  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name || '');
      setDescription(currentProject.description || '');
      setDeadline(currentProject.deadline ? currentProject.deadline.split('T')[0] : '');
      setStatus(currentProject.status || 'en cours');
      setPdf(null); // on réinitialise le fichier sélectionné
    } else {
      // Réinitialiser pour une création
      setName('');
      setDescription('');
      setDeadline('');
      setStatus('en cours');
      setPdf(null);
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    formData.append('status', status);
    if (pdf) formData.append('pdf', pdf);

    try {
      let response;

      if (currentProject) {
        // === MODIFICATION ===
        response = await API.put(`/projects/${currentProject._id}`, formData);
        // Mise à jour instantanée dans la liste
        setProjects(prev => prev.map(p => 
          p._id === response.data._id ? response.data : p
        ));
      } else {
        // === CRÉATION ===
        response = await API.post('/projects', formData);
        // Ajout en haut de la liste → apparition immédiate
        setProjects(prev => [response.data, ...prev]);
      }

      // Notification en temps réel pour les autres utilisateurs
      socket.emit('updateProject', {
        projectId: response.data._id,
        action: currentProject ? 'update' : 'create'
      });

      // Fermeture du modal
      closeModal();

    } catch (err) {
      console.error('Erreur sauvegarde projet :', err.response || err);
      alert('Erreur lors de la sauvegarde du projet. Vérifie la console.');
    }
  };

  return (
    <div className="bg-white p-8 rounded-xl max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        {currentProject ? 'Modifier le projet' : 'Nouveau projet'}
      </h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <input
          type="text"
          placeholder="Nom du projet"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />

        <textarea
          placeholder="Description du projet"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          rows="4"
          className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
        />

        <input
          type="date"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
          required
          className="w-full px-4 py-3 border border-gray-300 rounded-lg"
        />

        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          className="w-full px-4 py-3 border border-gray-300 rounded-lg"
        >
          <option value="en cours">En cours</option>
          <option value="fini">Terminé</option>
        </select>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            PDF du projet (facultatif)
          </label>
          <input
            type="file"
            accept=".pdf"
            onChange={(e) => setPdf(e.target.files[0] || null)}
            className="w-full text-sm text-gray-600 file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
          />
          {currentProject?.pdf && !pdf && (
            <p className="text-sm text-gray-600 mt-2">
              PDF actuel :{' '}
              <a
                href={`http://localhost:5000/${currentProject.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline hover:text-blue-800"
              >
                Voir le fichier actuel
              </a>
            </p>
          )}
        </div>

        <div className="flex justify-end space-x-4 pt-6">
          <button
            type="button"
            onClick={closeModal}
            className="px-6 py-3 border border-gray-300 rounded-lg hover:bg-gray-100 transition"
          >
            Annuler
          </button>
          <button
            type="submit"
            className="px-8 py-3 bg-green-600 hover:bg-green-700 text-white font-bold rounded-lg transition shadow-lg"
          >
            Sauvegarder
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProjectForm;
