// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null); // null = création, objet = modification

  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (projectId) => {
    if (!window.confirm('Voulez-vous vraiment supprimer ce projet ?')) return;

    try {
      await API.delete(`/projects/${projectId}`);
      refreshProjects(); // recharge la liste
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  return (
    <div className="space-y-8">

      {/* Bouton Ajouter (visible uniquement pour le prof) */}
      {isProf && (
        <div className="text-right mb-6">
          <button
            onClick={() => openModal()}
            className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white font-bold text-lg px-8 py-4 rounded-xl shadow-lg hover:shadow-2xl transition"
          >
            + Ajouter un nouveau projet
          </button>
        </div>
      )}

      {/* Liste des projets */}
      {projects.length === 0 ? (
        <p className="text-center text-gray-500 text-xl py-20">
          {isProf ? 'Aucun projet créé pour le moment.' : 'Aucun projet assigné.'}
        </p>
      ) : (
        <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
          {projects.map((project) => (
            <div
              key={project._id}
              className="bg-white rounded-2xl shadow-xl p-8 border border-gray-200 hover:shadow-2xl transition"
            >
              <h3 className="text-2xl font-bold text-indigo-800 mb-3">
                {project.name}
              </h3>
              <p className="text-gray-700 mb-4">{project.description || 'Pas de description'}</p>
              <p className="text-sm text-gray-600">
                Deadline : <strong>{new Date(project.deadline).toLocaleDateString('fr-FR')}</strong>
              </p>

              {/* PDF du sujet */}
              {project.pdf && (
                <a
                  href={`http://localhost:5000/${project.pdf}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-block mt-4 px-5 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Télécharger le sujet
                </a>
              )}

              {/* Boutons Modifier et Supprimer (prof uniquement) */}
              {isProf && (
                <div className="mt-8 flex gap-4 justify-end">
                  <button
                    onClick={() => openModal(project)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white font-bold px-6 py-3 rounded-lg shadow"
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => handleDelete(project._id)}
                    className="bg-red-600 hover:bg-red-700 text-white font-bold px-6 py-3 rounded-lg shadow"
                  >
                    Supprimer
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Modal pour Ajouter ou Modifier */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-10 rounded-3xl shadow-2xl max-w-2xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50"
      >
        <h2 className="text-3xl font-bold text-center mb-8 text-indigo-800">
          {currentProject ? 'Modifier le projet' : 'Créer un nouveau projet'}
        </h2>

        <ProjectForm
          currentProject={currentProject}
          closeModal={closeModal}
          refreshProjects={refreshProjects}
        />
      </Modal>
    </div>
  );
};

export default ProjectList;
