// src/components/TaskList.js
import { useState, useEffect } from 'react';
import TaskForm from './TaskForm';
import Modal from 'react-modal';
import API from '../services/api';
import socket from '../socket';

Modal.setAppElement('#root');

const TaskList = ({ projectId, user }) => {
  const [tasks, setTasks] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);
  const [showExport, setShowExport] = useState(false);

  const fetchTasks = async () => {
    try {
      const { data } = await API.get(`/projects/${projectId}/tasks`);
      setTasks(data || []);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchTasks();
    socket.on('projectUpdated', fetchTasks);
    return () => socket.off('projectUpdated', fetchTasks);
  }, [projectId]);

  const openModal = (task = null) => {
    setCurrentTask(task);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTask(null);
    setShowExport(false);
  };

  const toggleComplete = async (task) => {
    await API.put(`/tasks/${task._id}`, { completed: !task.completed });
    fetchTasks();
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file || !currentTask) return;

    const formData = new FormData();
    formData.append('file', file);

    await API.post(`/tasks/${currentTask._id}/files`, formData);
    fetchTasks();
    closeModal();
  };

  const isAdmin = user?.role === 'admin';
  const isAssigned = (task) => task.assignedTo && task.assignedTo.toString() === user?._id;

  return (
    <div className="mt-6 bg-gray-50 p-6 rounded-xl">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-2xl font-bold text-gray-800">Tâches</h3>
        {isAdmin && (
          <button onClick={() => openModal()} className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-3 rounded-lg">
            + Ajouter une tâche
          </button>
        )}
      </div>

      {tasks.length === 0 ? (
        <p className="text-gray-500 text-center py-8">Aucune tâche assignée.</p>
      ) : (
        <div className="space-y-4">
          {tasks.map(task => (
            <div key={task._id} className="bg-white p-5 rounded-lg shadow border">
              <h4 className="text-xl font-bold">{task.name}</h4>
              <p className="text-gray-600">{task.description || 'Pas de description'}</p>
              <p className="text-sm text-gray-500">Deadline : {new Date(task.deadline).toLocaleDateString('fr-FR')}</p>

              <div className="mt-3 flex items-center gap-4">
                <span className={`px-4 py-2 rounded-full text-sm font-medium ${task.completed ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>
                  {task.completed ? 'Terminée' : 'En cours'}
                </span>

                {isAssigned(task) && (
                  <button onClick={() => toggleComplete(task)} className="text-blue-600 font-medium">
                    {task.completed ? 'Marquer non terminée' : 'Marquer terminée'}
                  </button>
                )}
              </div>

              {task.pdf && (
                <a href={`http://localhost:5000/${task.pdf}`} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline block mt-2">
                  PDF du prof
                </a>
              )}

              {task.files && task.files.length > 0 && (
                <div className="mt-3">
                  <p className="font-medium">Fichiers exportés :</p>
                  {task.files.map((file, i) => (
                    <a key={i} href={`http://localhost:5000/${file}`} target="_blank" rel="noopener noreferrer" className="block text-green-600 underline text-sm">
                      {file.split('/').pop()}
                    </a>
                  ))}
                </div>
              )}

              <div className="mt-4 space-x-3">
                {(isAdmin || isAssigned(task)) && (
                  <button onClick={() => openModal(task)} className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded">
                    Modifier
                  </button>
                )}
                {isAssigned(task) && !showExport && (
                  <button onClick={() => { setCurrentTask(task); setShowExport(true); }} className="bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded">
                    Exporter mon travail
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      <Modal isOpen={modalIsOpen || showExport} onRequestClose={closeModal} className="bg-white p-8 rounded-xl shadow-2xl max-w-lg mx-auto mt-20 outline-none" overlayClassName="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
        {showExport ? (
          <div>
            <h2 className="text-2xl font-bold mb-6">Exporter votre travail</h2>
            <input type="file" onChange={handleFileUpload} className="block w-full text-sm text-gray-900 border border-gray-300 rounded-lg cursor-pointer bg-gray-50" />
            <button onClick={closeModal} className="mt-4 px-6 py-2 bg-gray-500 text-white rounded">Annuler</button>
          </div>
        ) : (
          <TaskForm closeModal={closeModal} currentTask={currentTask} projectId={projectId} refreshTasks={fetchTasks} />
        )}
      </Modal>
    </div>
  );
};

export default TaskList;
