// src/components/ProjectForm.js
import { useState, useEffect } from 'react';
import API from '../services/api';

const ProjectForm = ({ currentProject, closeModal, refreshProjects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [pdf, setPdf] = useState(null);

  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name);
      setDescription(currentProject.description || '');
      setDeadline(currentProject.deadline ? new Date(currentProject.deadline).toISOString().split('T')[0] : '');
    } else {
      setName(''); setDescription(''); setDeadline(''); setPdf(null);
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    if (pdf) formData.append('pdf', pdf);

    try {
      if (currentProject) {
        await API.put(`/projects/${currentProject._id}`, formData);
      } else {
        await API.post('/projects', formData);
      }
      refreshProjects();
      closeModal();
    } catch (err) {
      alert('Erreur de sauvegarde. Vérifie la console.');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-8">
      <h2 className="text-3xl font-bold text-center text-indigo-800">
        {currentProject ? 'Modifier le projet' : 'Nouveau projet'}
      </h2>

      <input type="text" placeholder="Nom du projet" value={name} onChange={e => setName(e.target.value)} required
        className="w-full p-5 border-2 border-gray-200 rounded-2xl text-lg focus:border-indigo-500 outline-none" />

      <textarea placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} rows="4"
        className="w-full p-5 border-2 border-gray-200 rounded-2xl text-lg focus:border-indigo-500 outline-none" />

      <input type="date" value={deadline} onChange={e => setDeadline(e.target.value)} required
        className="w-full p-5 border-2 border-gray-200 rounded-2xl text-lg focus:border-indigo-500 outline-none" />

      <input type="file" accept=".pdf" onChange={e => setPdf(e.target.files[0] || null)}
        className="block w-full text-sm file:mr-4 file:py-3 file:px-6 file:rounded-full file:bg-indigo-600 file:text-white" />

      <div className="flex justify-center gap-8 pt-6">
        <button type="button" onClick={closeModal}
          className="px-10 py-5 bg-gradient-to-r from-gray-100 to-gray-200 hover:from-gray-200 hover:to-gray-300 text-gray-700 font-bold rounded-2xl shadow-md hover:shadow-xl transition-all border border-gray-300">
          Annuler
        </button>
        <button type="submit"
          className="px-10 py-5 bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold rounded-2xl shadow-md hover:shadow-xl transition-all">
          {currentProject ? 'Sauvegarder' : 'Créer'}
        </button>
      </div>
    </form>
  );
};

export default ProjectForm;
