// src/components/TaskList.js
import { useState, useEffect } from 'react';
import TaskForm from './TaskForm';
import Modal from 'react-modal';
import API from '../services/api';
import socket from '../socket';

Modal.setAppElement('#root');

const TaskList = ({ projectId, user }) => {
  const [tasks, setTasks] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  // Fonction pour charger les tâches (appelée au montage + après chaque mise à jour)
  const fetchTasks = async () => {
    try {
      const { data } = await API.get(`/projects/${projectId}/tasks`); // Route correcte
      setTasks(data || []);
    } catch (err) {
      console.error("Erreur chargement tâches :", err);
      setTasks([]);
    }
  };

  useEffect(() => {
    if (!projectId) return;

    fetchTasks();

    // Écoute les mises à jour en temps réel (création, modification, suppression)
    const handleUpdate = () => {
      fetchTasks();
    };

    socket.on('projectUpdated', handleUpdate);
    socket.on('taskUpdated', handleUpdate); // Au cas où tu envoies un événement spécifique

    return () => {
      socket.off('projectUpdated', handleUpdate);
      socket.off('taskUpdated', handleUpdate);
    };
  }, [projectId]);

  const openModal = (task = null) => {
    setCurrentTask(task);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTask(null);
  };

  const handleDelete = async (id) => {
    try {
      await API.delete(`/tasks/${id}`);
      setTasks(prev => prev.filter(t => t._id !== id));
      socket.emit('updateProject', { projectId, action: 'taskDeleted' });
    } catch (err) {
      alert("Erreur suppression tâche");
    }
  };

  const isAdmin = user?.role === 'admin';

  return (
    <div className="mt-6 bg-gray-50 p-4 rounded-lg">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-xl font-semibold text-gray-800">Tâches du projet</h3>
        {isAdmin && (
          <button
            onClick={() => openModal()}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-medium shadow"
          >
            + Ajouter une tâche
          </button>
        )}
      </div>

      {tasks.length === 0 ? (
        <p className="text-gray-500 italic">Aucune tâche pour le moment.</p>
      ) : (
        <div className="space-y-3">
          {tasks.map(task => (
            <div key={task._id} className="bg-white p-4 rounded-lg shadow border border-gray-200">
              <h4 className="font-bold text-lg">{task.name}</h4>
              <p className="text-gray-600">{task.description || 'Pas de description'}</p>
              <p className="text-sm text-gray-500 mt-1">
                Deadline : {new Date(task.deadline).toLocaleDateString('fr-FR')}
              </p>
              <p className="mt-2">
                Statut :{' '}
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${task.completed ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>
                  {task.completed ? 'Terminée' : 'En cours'}
                </span>
              </p>
              {task.pdf && (
                <a
                  href={`http://localhost:5000/${task.pdf}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 underline text-sm block mt-2"
                >
                  Télécharger le PDF
                </a>
              )}
              <div className="mt-3 space-x-2">
                {(isAdmin || task.assignedTo === user?._id) && (
                  <button
                    onClick={() => openModal(task)}
                    className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded text-sm"
                  >
                    Modifier
                  </button>
                )}
                {isAdmin && (
                  <button
                    onClick={() => handleDelete(task._id)}
                    className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm"
                  >
                    Supprimer
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-8 rounded-xl shadow-2xl max-w-2xl mx-auto mt-20 outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center"
      >
        <TaskForm
          closeModal={closeModal}
          currentTask={currentTask}
          projectId={projectId}
          setTasks={setTasks}
          tasks={tasks}
          refreshTasks={fetchTasks} // On passe la fonction pour forcer le refresh
        />
      </Modal>
    </div>
  );
};

export default TaskList;
