// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet ?')) return;
    await API.delete(`/projects/${id}`);
    refreshProjects();
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    await API.post(`/projects/${projectId}/submit`, formData);
    refreshProjects();
  };

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  return (
    <div>
      {/* Bouton Ajouter (prof uniquement) */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="bg-gradient-to-r from-indigo-600 to-purple-700 text-white text-xl font-bold px-10 py-5 rounded-2xl shadow-2xl hover:shadow-purple-500 transition"
          >
            + Ajouter un nouveau projet
          </button>
        </div>
      )}

      {/* Liste des projets */}
      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
        {projects.map((project) => (
          <div key={project._id} className="bg-white rounded-3xl shadow-2xl p-8 border border-gray-200 hover:scale-105 transition">
            <h3 className="text-3xl font-bold text-indigo-800 mb-3">{project.name}</h3>
            <p className="text-gray-600 mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-sm font-medium text-gray-700">
              Deadline : <span className="text-indigo-600">{new Date(project.deadline).toLocaleDateString('fr-FR')}</span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mt-6 px-8 py-4 bg-gradient-to-r from-blue-600 to-blue-800 text-white font-bold rounded-xl shadow-lg hover:shadow-blue-600"
              >
                Télécharger le sujet
              </a>
            )}

            {/* Étudiant : déposer son rendu */}
            {!isProf && (
              <div className="mt-8 p-6 bg-gradient-to-r from-green-50 to-emerald-50 rounded-2xl">
                {hasSubmitted(project) ? (
                  <div className="text-green-700 font-bold text-xl text-center">
                    Compte-rendu déposé !
                  </div>
                ) : (
                  <div>
                    <p className="font-bold text-gray-800 mb-3">Déposez votre compte-rendu :</p>
                    <input
                      type="file"
                      accept=".pdf,.zip,.docx"
                      onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
                      className="block w-full text-sm text-gray-900 file:mr-4 file:py-3 file:px-6 file:rounded-full file:border-0 file:bg-gradient-to-r file:from-green-600 file:to-emerald-700 file:text-white hover:file:from-green-700 hover:file:to-emerald-800 cursor-pointer"
                    />
                  </div>
                )}
              </div>
            )}

            {/* Prof : voir les rendus */}
            {isProf && project.submissions?.length > 0 && (
              <div className="mt-6 bg-gradient-to-r from-purple-50 to-pink-50 p-6 rounded-2xl">
                <p className="font-bold text-purple-800 text-lg">
                  {project.submissions.length} rendu(s)
                </p>
                {project.submissions.map((s, i) => (
                  <div key={i} className="text-sm mt-2">
                    <strong>{s.student.name}</strong>
                    <a href={`http://localhost:5000/${s.file}`} target="_blank" className="text-blue-600 underline ml-3">
                      Voir le fichier
                    </a>
                  </div>
                ))}
              </div>
            )}

            {/* Boutons Modifier/Supprimer (prof) */}
            {isProf && (
  <div className="mt-10 flex justify-end gap-4">
    <button
      onClick={() => openModal(project)}
      className="flex items-center gap-2 px-6 py-3 bg-blue-100 hover:bg-blue-200 text-blue-800 font-semibold rounded-xl shadow-md hover:shadow-lg transition-all duration-200 border border-blue-300"
    >
      Modifier
    </button>
    <button
      onClick={() => handleDelete(project._id)}
      className="flex items-center gap-2 px-6 py-3 bg-purple-100 hover:bg-purple-200 text-purple-800 font-semibold rounded-xl shadow-md hover:shadow-lg transition-all duration-200 border border-purple-300"
    >
      Supprimer
    </button>
  </div>
)}
          </div>
        ))}
      </div>

      {/* Modal Ajouter/Modifier */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-10 rounded-3xl shadow-2xl max-w-2xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
