// src/components/Dashboard.js
import { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import API from '../services/api';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Récupère l'utilisateur + les projets
  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);

      // 1. Récupérer les projets (avec populate complet grâce au backend corrigé)
      const projectsRes = await API.get('/projects');
      setProjects(projectsRes.data || []);

      // 2. Récupérer les infos de l'utilisateur connecté
      const userRes = await API.get('/users/me');
      setUser(userRes.data);

    } catch (err) {
      console.error('Erreur Dashboard :', err);
      if (err.response?.status === 401) {
        // Token expiré ou absent → déconnexion
        localStorage.removeItem('token');
        window.location.href = '/login';
      } else {
        setError('Impossible de charger les données. Veuillez réessayer.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Chargement au montage
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      window.location.href = '/login';
      return;
    }
    fetchData();
  }, []);

  // Fonction passée à ProjectList pour recharger après création/modification/soumission
  const refreshProjects = () => {
    fetchData();
  };

  // États de chargement ou erreur
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="text-3xl font-bold text-indigo-700">Chargement en cours...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-red-50 to-pink-100">
        <div className="text-center">
          <p className="text-2xl font-bold text-red-700 mb-4">{error}</p>
          <button
            onClick={fetchData}
            className="px-8 py-4 bg-indigo-600 text-white font-bold rounded-full hover:bg-indigo-700 transition"
          >
            Réessayer
          </button>
        </div>
      </div>
    );
  }

  const isProf = user?.role === 'admin';
  const userName = user?.name || 'Étudiant';

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header personnalisé */}
      <div className="bg-gradient-to-r from-indigo-600 to-purple-700 text-white py-16 px-8 shadow-2xl">
        <div className="max-w-7xl mx-auto text-center">
          <h1 className="text-5xl md:text-6xl font-extrabold mb-4">
            {isProf ? 'Espace Professeur' : `Bonjour, ${userName}`}
          </h1>
          <p className="text-xl md:text-2xl opacity-90">
            {isProf
              ? 'Créez des projets et consultez les rendus de vos étudiants'
              : 'Déposez vos comptes-rendus avant la date limite'}
          </p>
        </div>
      </div>

      {/* Contenu principal */}
      <div className="max-w-7xl mx-auto p-8 -mt-10">
        <ProjectList
          projects={projects}
          user={user}
          refreshProjects={refreshProjects}
        />
      </div>
    </div>
  );
};

export default Dashboard;
