import { useState, useEffect } from 'react';
import ProjectForm from './ProjectForm';
import TaskList from './TaskList';
import Modal from 'react-modal';
import API from '../services/api';
import socket from '../socket';

Modal.setAppElement('#root');

const ProjectList = ({ projects, setProjects, user }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => setModalIsOpen(false);

  const handleDelete = async (id) => {
    try {
      await API.delete(`/projects/${id}`);
      setProjects(projects.filter((p) => p._id !== id));
      socket.emit('updateProject', { projectId: id, action: 'delete' });
    } catch (err) {
      alert("Erreur lors de la suppression");
    }
  };

  useEffect(() => {
    socket.on('projectUpdated', (data) => {
      setProjects((prev) =>
        prev.map((p) => (p._id === data.projectId ? { ...p, ...data } : p))
      );
    });
    return () => socket.off('projectUpdated');
  }, []);

  // Protection contre user null + optional chaining partout
  const isAdmin = user?.role === 'admin';

  return (
    <div>
      {/* Bouton "Ajouter Projet" visible seulement pour admin */}
      {isAdmin && (
        <button
          onClick={() => openModal()}
          className="bg-blue-600 hover:bg-blue-700 text-white font-medium px-5 py-2 rounded-lg mb-6 shadow"
        >
          + Ajouter un Projet
        </button>
      )}

      {/* Liste des projets */}
      {projects.length === 0 ? (
        <p className="text-gray-500 text-center py-10">
          Aucun projet disponible pour le moment.
        </p>
      ) : (
        projects.map((project) => (
          <div
            key={project._id}
            className="bg-white p-6 mb-6 rounded-xl shadow-md border border-gray-200"
          >
            <h2 className="text-2xl font-bold text-gray-800">{project.name}</h2>
            <p className="text-gray-600 mt-2">{project.description}</p>
            <p className="text-sm text-gray-500 mt-2">
              Deadline : {new Date(project.deadline).toLocaleDateString('fr-FR')}
            </p>
            <p className="mt-1">
              Statut :{' '}
              <span
                className={`inline-block px-3 py-1 rounded-full text-sm font-medium ${
                  project.status === 'fini'
                    ? 'bg-green-100 text-green-800'
                    : 'bg-yellow-100 text-yellow-800'
                }`}
              >
                {project.status}
              </span>
            </p>

            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 underline text-sm"
              >
                Télécharger le PDF
              </a>
            )}

            <div className="mt-4 space-x-3">
              <button
                onClick={() => openModal(project)}
                className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded"
              >
                Modifier
              </button>
              {isAdmin && (
                <button
                  onClick={() => handleDelete(project._id)}
                  className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
                >
                  Supprimer
                </button>
              )}
            </div>

            {/* Tâches du projet */}
            <TaskList projectId={project._id} user={user} />
          </div>
        ))
      )}

      {/* Modal de création/édition */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-8 rounded-xl shadow-2xl max-w-2xl mx-auto mt-20 outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center"
      >
        <ProjectForm
          closeModal={closeModal}
          currentProject={currentProject}
          setProjects={setProjects}
          projects={projects}
        />
      </Modal>
    </div>
  );
};

export default ProjectList;
