// src/components/Dashboard.js
import { useState } from 'react';
import ProjectList from './ProjectList';
import ProgressChart from './ProgressChart';
import ExportButtons from './ExportButtons';

const Dashboard = ({ projects = [], setProjects, user }) => {  // ← projects = [] par défaut
  const [filter, setFilter] = useState('all');

  // Protection contre undefined + filtre
  const safeProjects = Array.isArray(projects) ? projects : [];
  const filteredProjects = safeProjects.filter(p => 
    filter === 'all' || p.status === filter
  );

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-4xl font-bold mb-8 text-gray-800">
        Tableau de bord – {user?.name || 'Utilisateur'}
        {user?.role === 'admin' && <span className="text-green-600"> (Professeur)</span>}
      </h1>

      {/* Filtre */}
      <div className="mb-6">
        <select 
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="p-3 border border-gray-300 rounded-lg text-lg"
        >
          <option value="all">Tous les projets</option>
          <option value="en cours">En cours</option>
          <option value="fini">Terminé</option>
        </select>
      </div>

      {/* Liste des projets */}
      <ProjectList 
        projects={filteredProjects} 
        setProjects={setProjects} 
        user={user} 
      />

      {/* Graphique + Export */}
      <div className="mt-12 grid md:grid-cols-2 gap-8">
        <ProgressChart projects={safeProjects} />
        <ExportButtons projects={safeProjects} />
      </div>
    </div>
  );
};

export default Dashboard;
