import jsPDF from 'jspdf';
import { createObjectCsvWriter } from 'csv-writer'; // Note: Pour browser, utilise une lib comme papaparse si besoin, mais ici simulation backend pour CSV.

const ExportButtons = ({ projects }) => {
  const exportPDF = () => {
    const doc = new jsPDF();
    projects.forEach((p, i) => {
      doc.text(`${i+1}. ${p.name} - Status: ${p.status}`, 10, 10 + i*10);
    });
    doc.save('rapports.pdf');
  };

  const exportCSV = () => {
    // Simulation : En vrai, appelle une route backend pour générer CSV
    const csvData = projects.map(p => ({ name: p.name, status: p.status, deadline: p.deadline }));
    const csvWriter = createObjectCsvWriter({
      path: 'rapports.csv', // En browser, utilise Blob pour download
      header: [{id: 'name', title: 'Name'}, {id: 'status', title: 'Status'}, {id: 'deadline', title: 'Deadline'}]
    });
    csvWriter.writeRecords(csvData).then(() => console.log('CSV écrit'));
    // Pour browser : Utilise une fonction pour download Blob
  };

  return (
    <div className="mt-4">
      <button onClick={exportPDF} className="bg-purple-500 text-white p-2 mr-2">Exporter PDF</button>
      <button onClick={exportCSV} className="bg-purple-500 text-white p-2">Exporter CSV</button>
    </div>
  );
};

export default ExportButtons;
