// src/components/ProjectList.js → VERSION FINALE 100% FONCTIONNELLE (aucune erreur)
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalOpen, setModalOpen] = useState(false);
  const [editProject, setEditProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const isProf = user?.role === 'admin';

  // Sécurité maximale contre les données corrompues
  const mySubmission = (project) => {
    if (!project?.submissions || !user?._id) return null;
    return project.submissions.find((s) => {
      const studentId = s?.student?._id || s?.student;
      return studentId && studentId.toString() === user._id.toString();
    });
  };

  const openSubmitModal = (project) => {
    setSelectedProject(project);
    setFileName(mySubmission(project)?.customName || '');
    setFile(null);
    setEditProject(null);
    setModalOpen(true);
  };

  const submitFile = async () => {
    if (!file) return alert('Veuillez choisir un fichier');
    if (!fileName.trim()) return alert('Donnez un nom au fichier');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('Rendu déposé avec succès !');
      setModalOpen(false);
      refreshProjects();
    } catch (err) {
      console.error(err);
      alert('Erreur lors du dépôt. Vérifiez votre connexion ou le fichier.');
    }
  };

  return (
    <div className="space-y-12">
      {/* Bouton Créer un projet (prof) */}
      {isProf && (
        <div className="text-center">
          <button
            onClick={() => {
              setEditProject(null);
              setModalOpen(true);
            }}
            className="px-12 py-5 bg-white text-indigo-600 font-bold text-xl rounded-full shadow-2xl hover:shadow-purple-500/50 transition transform hover:scale-105"
          >
            + Créer un nouveau projet
          </button>
        </div>
      )}

      {/* Grille de cartes magnifiques */}
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-10">
        {projects.map((project) => {
          const submission = mySubmission(project);

          return (
            <div
              key={project._id}
              className="bg-white rounded-3xl shadow-2xl overflow-hidden transform hover:scale-105 transition duration-300"
            >
              {/* Header violet */}
              <div className="bg-gradient-to-r from-indigo-500 to-purple-600 p-8 text-white">
                <h3 className="text-2xl font-bold">{project.name}</h3>
                <p className="text-lg mt-2 opacity-90">
                  Deadline : {new Date(project.deadline).toLocaleDateString('fr-FR')}
                </p>
              </div>

              {/* Contenu */}
              <div className="p-8 space-y-6">
                <p className="text-gray-700 text-lg">
                  {project.description || 'Aucune description'}
                </p>

                {project.pdf && (
                  <a
                    href={`http://localhost:5000/${project.pdf}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="block text-indigo-600 font-semibold hover:underline"
                  >
                    Sujet PDF
                  </a>
                )}

                {/* Étudiant */}
                {!isProf && (
                  <div className="pt-6 border-t">
                    {submission ? (
                      <div className="bg-green-50 rounded-2xl p-6 text-center">
                        <p className="font-bold text-green-800 text-lg">Rendu déposé</p>
                        <p className="text-gray-700 mt-2">{submission.customName}</p>
                        <button
                          onClick={() => openSubmitModal(project)}
                          className="mt-4 text-indigo-600 underline"
                        >
                          Modifier mon rendu
                        </button>
                      </div>
                    ) : (
                      <button
                        onClick={() => openSubmitModal(project)}
                        className="w-full py-5 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-bold text-xl rounded-2xl shadow-xl hover:shadow-2xl transition"
                      >
                        Déposer mon rendu
                      </button>
                    )}
                  </div>
                )}

                {/* Professeur */}
                {isProf && (
                  <div className="pt-6 border-t space-y-5">
                    <p className="text-center font-bold text-purple-700 text-xl">
                      {project.submissions?.length || 0} rendu(s) reçu(s)
                    </p>

                    <div className="flex gap-4">
                      <button
                        onClick={() => {
                          setEditProject(project);
                          setModalOpen(true);
                        }}
                        className="flex-1 py-4 bg-gray-700 text-white font-semibold rounded-xl hover:bg-gray-800 transition"
                      >
                        Modifier
                      </button>
                      <button
                        onClick={() => {
                          if (window.confirm('Supprimer ce projet et tous les rendus ?')) {
                            API.delete(`/projects/${project._id}`)
                              .then(refreshProjects)
                              .catch(() => alert('Erreur suppression'));
                          }
                        }}
                        className="flex-1 py-4 bg-red-600 text-white font-semibold rounded-xl hover:bg-red-700 transition"
                      >
                        Supprimer
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* Modal dépôt / création */}
      <Modal
        isOpen={modalOpen}
        onRequestClose={() => setModalOpen(false)}
        className="bg-white p-12 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black/70 flex items-center justify-center z-50"
      >
        {editProject === null ? (
          <div className="space-y-8">
            <h2 className="text-4xl font-bold text-center text-indigo-800">
              Déposer mon rendu
            </h2>

            <input
              type="text"
              placeholder="Ex: TP1 - Votre Nom.pdf"
              value={fileName}
              onChange={(e) => setFileName(e.target.value)}
              className="w-full px-6 py-5 border-2 border-gray-300 rounded-xl text-lg focus:border-indigo-600 outline-none"
            />

            <input
              type="file"
              accept=".pdf,.docx,.zip,.jpg,.jpeg,.png"
              onChange={(e) => setFile(e.target.files?.[0] || null)}
              className="w-full text-lg"
            />

            <div className="flex gap-6">
              <button
                onClick={submitFile}
                className="flex-1 py-5 bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold text-xl rounded-xl shadow-xl hover:shadow-2xl transition"
              >
                Déposer
              </button>
              <button
                onClick={() => setModalOpen(false)}
                className="flex-1 py-5 bg-gray-600 text-white font-bold text-xl rounded-xl hover:bg-gray-700 transition"
              >
                Annuler
              </button>
            </div>
          </div>
        ) : (
          <ProjectForm
            currentProject={editProject}
            closeModal={() => {
              setModalOpen(false);
              setEditProject(null);
            }}
            refreshProjects={refreshProjects}
          />
        )}
      </Modal>
    </div>
  );
};

export default ProjectList;
