import { useState } from 'react';
import ProjectForm from './ProjectForm';
import TaskList from './TaskList';
import Modal from 'react-modal';
import API from '../services/api';
import socket from '../socket';

Modal.setAppElement('#root');

const ProjectList = ({ projects, setProjects, user }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => setModalIsOpen(false);

  const handleDelete = async (id) => {
    await API.delete(`/projects/${id}`);
    setProjects(projects.filter(p => p._id !== id));
    socket.emit('updateProject', { projectId: id, action: 'delete' });
  };

  useEffect(() => {
    socket.on('projectUpdated', (data) => {
      // Mettre à jour les projects en temps réel
      setProjects(prev => prev.map(p => p._id === data.projectId ? { ...p, ...data } : p));
    });
    return () => socket.off('projectUpdated');
  }, []);

  return (
    <div>
      {user.role === 'admin' && <button onClick={() => openModal()} className="bg-blue-500 text-white p-2 mb-4">Ajouter Projet</button>}
      {projects.map(project => (
        <div key={project._id} className="bg-white p-4 mb-4 rounded shadow">
          <h2 className="text-xl">{project.name}</h2>
          <p>{project.description}</p>
          <p>Deadline: {new Date(project.deadline).toLocaleDateString()}</p>
          <p>Status: {project.status}</p>
          {project.pdf && <a href={project.pdf} download>Télécharger PDF</a>}
          <button onClick={() => openModal(project)} className="bg-yellow-500 text-white p-1 mr-2">Modifier</button>
          <button onClick={() => handleDelete(project._id)} className="bg-red-500 text-white p-1">Supprimer</button>
          <TaskList projectId={project._id} user={user} />
        </div>
      ))}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="bg-white p-6 rounded shadow-md m-auto max-w-lg">
        <ProjectForm closeModal={closeModal} currentProject={currentProject} setProjects={setProjects} projects={projects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
