// src/components/ProjectForm.js → VERSION FINALE PARFAITE (bouton bien en dessous)
import { useState, useEffect } from 'react';
import API from '../services/api';

const ProjectForm = ({ currentProject, closeModal, refreshProjects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [pdf, setPdf] = useState(null);
  const [currentFileName, setCurrentFileName] = useState('');

  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name || '');
      setDescription(currentProject.description || '');
      setDeadline(currentProject.deadline ? new Date(currentProject.deadline).toISOString().split('T')[0] : '');
      setCurrentFileName(currentProject.pdf ? currentProject.pdf.split('/').pop() : '');
    } else {
      setName('');
      setDescription('');
      setDeadline('');
      setPdf(null);
      setCurrentFileName('');
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    if (pdf) formData.append('pdf', pdf);

    try {
      if (currentProject) {
        await API.put(`/projects/${currentProject._id}`, formData);
      } else {
        await API.post('/projects', formData);
      }
      refreshProjects();
      closeModal();
    } catch (err) {
      alert('Erreur lors de la sauvegarde');
    }
  };

  return (
    <div className="bg-white rounded-3xl p-10 max-w-2xl w-full mx-auto shadow-2xl">
      <h2 className="text-4xl font-bold text-center text-indigo-800 mb-10">
        {currentProject ? 'Modifier le projet' : 'Nouveau projet'}
      </h2>

      <form onSubmit={handleSubmit} className="space-y-8">

        {/* Nom du projet */}
        <div>
          <input
            type="text"
            placeholder="Nom du projet"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            className="w-full px-6 py-5 rounded-2xl border-2 border-gray-200 focus:border-indigo-600 outline-none text-lg transition"
          />
        </div>

        {/* Description */}
        <div>
          <textarea
            placeholder="Description (facultatif)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
            className="w-full px-6 py-5 rounded-2xl border-2 border-gray-200 focus:border-indigo-600 outline-none text-lg resize-none transition"
          />
        </div>

        {/* Deadline */}
        <div>
          <input
            type="date"
            value={deadline}
            onChange={(e) => setDeadline(e.target.value)}
            required
            className="w-full px-6 py-5 rounded-2xl border-2 border-gray-200 focus:border-indigo-600 outline-none text-lg transition"
          />
        </div>

        {/* PDF du sujet – BOUTON BIEN EN DESSOUS */}
        <div className="space-y-6">
          <label className="block text-lg font-medium text-gray-700">
            PDF du sujet (facultatif)
          </label>

          {/* Bouton bien espacé en dessous */}
          <div className="block">
            <label className="cursor-pointer">
              <span className="inline-block px-12 py-5 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-lg rounded-full shadow-xl hover:shadow-2xl transition transform hover:scale-105">
                Choisir un PDF
              </span>
              <input
                type="file"
                accept=".pdf"
                onChange={(e) => setPdf(e.target.files[0])}
                className="hidden"
              />
            </label>
          </div>

          {/* Nom du fichier affiché en dessous */}
          <p className="text-sm text-gray-600 pl-1">
            {pdf 
              ? pdf.name 
              : currentFileName 
                ? `Fichier actuel : ${currentFileName}` 
                : 'Aucun fichier sélectionné'
            }
          </p>
        </div>

        {/* Boutons Annuler / Sauvegarder */}
        <div className="flex justify-center gap-8 pt-10">
          <button
            type="button"
            onClick={closeModal}
            className="px-12 py-5 bg-gradient-to-r from-gray-300 to-gray-500 text-white font-bold rounded-full shadow-lg hover:shadow-xl transition transform hover:scale-105"
          >
            Annuler
          </button>

          <button
            type="submit"
            className="px-14 py-5 bg-gradient-to-r from-pink-500 via-purple-600 to-indigo-600 text-white font-bold text-xl rounded-full shadow-2xl hover:shadow-purple-500/50 transition transform hover:scale-110"
          >
            {currentProject ? 'Mettre à jour' : 'Créer le projet'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProjectForm;
