// src/components/ProjectList.js → VERSION FINALE DASHBOARD
import ProjectCard from './ProjectCard';

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const isProf = user?.role === 'admin';

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-indigo-50 to-blue-50 p-8">
      {isProf && (
        <div className="text-center mb-12">
          <button className="px-10 py-6 bg-gradient-to-r from-indigo-600 to-purple-700 hover:from-indigo-700 hover:to-purple-800 text-white font-bold text-xl rounded-full shadow-2xl transition transform hover:scale-105">
            + Ajouter un projet
          </button>
        </div>
      )}

      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map(project => (
          <ProjectCard key={project._id} project={project} user={user} refreshProjects={refreshProjects} />
        ))}
      </div>
    </div>
  );
};

export default ProjectList;
