import { useState, useEffect } from 'react';
import TaskForm from './TaskForm';
import Modal from 'react-modal';
import API from '../services/api';
import socket from '../socket';

const TaskList = ({ projectId, user }) => {
  const [tasks, setTasks] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTask, setCurrentTask] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      const { data } = await API.get(`/tasks/project/${projectId}`);
      setTasks(data);
    };
    fetchTasks();
    socket.on('projectUpdated', (data) => {
      if (data.projectId === projectId) fetchTasks();
    });
    return () => socket.off('projectUpdated');
  }, [projectId]);

  const openModal = (task = null) => {
    setCurrentTask(task);
    setModalIsOpen(true);
  };

  const closeModal = () => setModalIsOpen(false);

  const handleDelete = async (id) => {
    await API.delete(`/tasks/${id}`);
    setTasks(tasks.filter(t => t._id !== id));
    socket.emit('updateProject', { projectId, action: 'taskDelete' });
  };

  return (
    <div className="mt-4">
      <h3 className="text-lg">Tâches</h3>
      {user.role === 'admin' && <button onClick={() => openModal()} className="bg-blue-500 text-white p-1 mb-2">Ajouter Tâche</button>}
      {tasks.map(task => (
        <div key={task._id} className="bg-gray-200 p-2 mb-2 rounded">
          <h4>{task.name}</h4>
          <p>{task.description}</p>
          <p>Deadline: {new Date(task.deadline).toLocaleDateString()}</p>
          <p>Complétée: {task.completed ? 'Oui' : 'Non'}</p>
          {task.pdf && <a href={task.pdf} download>PDF</a>}
          <button onClick={() => openModal(task)} className="bg-yellow-500 text-white p-1 mr-2">Modifier</button>
          <button onClick={() => handleDelete(task._id)} className="bg-red-500 text-white p-1">Supprimer</button>
        </div>
      ))}
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="bg-white p-6 rounded shadow-md m-auto max-w-lg">
        <TaskForm closeModal={closeModal} currentTask={currentTask} projectId={projectId} setTasks={setTasks} tasks={tasks} />
      </Modal>
    </div>
  );
};

export default TaskList;
