// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous ses fichiers ?')) return;
    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du projet');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      await API.post(`/projects/${projectId}/submit`, formData);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt du fichier');
    }
  };

  const handleDeleteSubmission = async (projectId) => {
    if (!window.confirm('Supprimer votre rendu ?')) return;
    try {
      await API.delete(`/projects/${projectId}/submission`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression du rendu');
    }
  };

  const hasSubmitted = (project) =>
    project.submissions?.some(s => s.student._id === user._id);

  const getMySubmission = (project) =>
    project.submissions?.find(s => s.student._id === user._id);

  return (
    <div className="p-8">
      {/* Bouton Ajouter – Prof */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="px-8 py-4 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-xl rounded-xl shadow-lg hover:shadow-xl transition"
          >
            + Ajouter un nouveau projet
          </button>
        </div>
      )}

      {/* Grille des projets */}
      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3">
        {projects.map((project) => (
          <div
            key={project._id}
            className="bg-white rounded-2xl shadow-xl p-8 border border-gray-200 hover:shadow-2xl transition"
          >
            <h3 className="text-2xl font-bold text-indigo-800 mb-3">{project.name}</h3>
            <p className="text-gray-600 mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-sm font-medium text-gray-700 mb-6">
              Deadline : <span className="text-indigo-600 font-bold">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {/* Télécharger le sujet */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-6 px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition"
              >
                Télécharger le sujet
              </a>
            )}

            {/* Étudiant : dépôt / modification / suppression */}
            {!isProf && (
              <div className="mt-6 p-5 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-200">
                {hasSubmitted(project) ? (
                  <div className="text-center space-y-4">
                    <p className="text-green-700 font-bold text-lg">Rendu déposé !</p>
                    {getMySubmission(project)?.file && (
                      <a
                        href={`http://localhost:5000/${getMySubmission(project).file}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-blue-600 underline text-sm block"
                      >
                        Voir mon fichier
                      </a>
                    )}
                    <div className="flex justify-center gap-4">
                      <label className="cursor-pointer">
                        <input
                          type="file"
                          accept=".pdf,.zip,.docx"
                          onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
                          className="hidden"
                        />
                        <span className="px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition">
                          Modifier mon rendu
                        </span>
                      </label>
                      <button
                        onClick={() => handleDeleteSubmission(project._id)}
                        className="px-6 py-3 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition"
                      >
                        Supprimer
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="text-center">
                    <label className="cursor-pointer">
                      <input
                        type="file"
                        accept=".pdf,.zip,.docx"
                        onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
                        className="hidden"
                      />
                      <span className="inline-block px-8 py-4 bg-green-600 text-white font-bold rounded-xl hover:bg-green-700 transition">
                        Déposer mon travail
                      </span>
                    </label>
                  </div>
                )}
              </div>
            )}

            {/* Prof : liste des rendus */}
            {isProf && project.submissions?.length > 0 && (
              <div className="mt-6 bg-purple-50 p-5 rounded-xl border border-purple-200">
                <p className="font-bold text-purple-800 mb-3">
                  {project.submissions.length} rendu(s)
                </p>
                {project.submissions.map((s, i) => (
                  <div key={i} className="text-sm bg-white p-3 rounded mb-2">
                    <strong>{s.student.name}</strong>
                    <a href={`http://localhost:5000/${s.file}`} target="_blank" className="text-blue-600 underline ml-3">
                      Voir
                    </a>
                  </div>
                ))}
              </div>
            )}

            {/* Boutons prof – taille normale et élégants */}
            {isProf && (
              <div className="flex justify-end gap-4 mt-8">
                <button
                  onClick={() => openModal(project)}
                  className="px-6 py-3 bg-gradient-to-r from-blue-100 to-cyan-100 hover:from-blue-200 hover:to-cyan-200 text-blue-800 font-bold rounded-lg border border-blue-300 transition"
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(project._id)}
                  className="px-6 py-3 bg-gradient-to-r from-purple-100 to-pink-100 hover:from-purple-200 hover:to-pink-200 text-purple-800 font-bold rounded-lg border border-purple-300 transition"
                >
                  Supprimer
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal normal – élégant */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-10 rounded-2xl shadow-2xl max-w-2xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
