import jsPDF from 'jspdf';

const ExportButtons = ({ projects }) => {
  const exportPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text('Rapport des Projets - Suivi Étudiant', 20, 20);

    projects.forEach((p, i) => {
      doc.setFontSize(12);
      doc.text(
        `${i + 1}. ${p.name} — Statut: ${p.status} — Deadline: ${new Date(
          p.deadline
        ).toLocaleDateString()}`,
        20,
        40 + i * 15
      );
    });

    doc.save('rapport-projets.pdf');
  };

  const exportCSV = () => {
    let csv = 'Nom du projet,Statut,Deadline,Nombre de tâches,Tâches complétées\n';
    projects.forEach((p) => {
      const completed = p.tasks ? p.tasks.filter((t) => t.completed).length : 0;
      csv += `"${p.name}","${p.status}","${new Date(p.deadline).toLocaleDateString()}","${
        p.tasks ? p.tasks.length : 0
      }","${completed}"\n`;
    });

    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'rapport-projets.csv';
    link.click();
  };

  return (
    <div className="mt-mt-8 mb-6 space-x-4">
      <button
        onClick={exportPDF}
        className="bg-indigo-600 hover:bg-indigo-700 text-white px-5 py-2 rounded-lg shadow"
      >
        Exporter en PDF
      </button>
      <button
        onClick={exportCSV}
        className="bg-green-600 hover:bg-green-700 text-white px-5 py-2 rounded-lg shadow"
      >
        Exporter en CSV
      </button>
    </div>
  );
};

export default ExportButtons;
