// src/components/ProjectForm.js
import { useState, useEffect } from 'react';
import API from '../services/api';

const ProjectForm = ({ currentProject, closeModal, refreshProjects }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [pdf, setPdf] = useState(null);

  useEffect(() => {
    if (currentProject) {
      setName(currentProject.name || '');
      setDescription(currentProject.description || '');
      setDeadline(currentProject.deadline ? new Date(currentProject.deadline).toISOString().split('T')[0] : '');
    } else {
      setName('');
      setDescription('');
      setDeadline('');
      setPdf(null);
    }
  }, [currentProject]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    if (pdf) formData.append('pdf', pdf);

    try {
      if (currentProject) {
        // MODIFICATION
        await API.put(`/projects/${currentProject._id}`, formData);
      } else {
        // CRÉATION
        await API.post('/projects', formData);
      }
      refreshProjects();
      closeModal();
    } catch (err) {
      console.error("Erreur sauvegarde :", err.response || err);
      alert('Erreur lors de la sauvegarde. Vérifie la console.');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <input
        type="text"
        placeholder="Nom du projet"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
        className="w-full p-4 border-2 border-gray-300 rounded-xl text-lg focus:border-indigo-600 outline-none"
      />

      <textarea
        placeholder="Description (facultatif)"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        rows="4"
        className="w-full p-4 border-2 border-gray-300 rounded-xl text-lg focus:border-indigo-600 outline-none"
      />

      <input
        type="date"
        value={deadline}
        onChange={(e) => setDeadline(e.target.value)}
        required
        className="w-full p-4 border-2 border-gray-300 rounded-xl text-lg focus:border-indigo-600 outline-none"
      />

      <div>
        <label className="block text-lg font-medium mb-2">PDF du sujet (facultatif)</label>
        <input
          type="file"
          accept=".pdf"
          onChange={(e) => setPdf(e.target.files[0])}
          className="block w-full text-sm text-gray-700 file:mr-4 file:py-3 file:px-6 file:rounded-full file:border-0 file:bg-indigo-600 file:text-white hover:file:bg-indigo-700"
        />
        {currentProject?.pdf && (
          <a href={`http://localhost:5000/${currentProject.pdf}`} target="_blank" className="text-blue-600 underline text-sm block mt-2">
            Voir le fichier actuel
          </a>
        )}
      </div>

      <div className="flex justify-end gap-4 pt-6">
        <button
          type="button"
          onClick={closeModal}
          className="px-8 py-4 bg-gray-500 text-white rounded-xl font-bold hover:bg-gray-600"
        >
          Annuler
        </button>
        <button
          type="submit"
          className="px-10 py-4 bg-gradient-to-r from-green-600 to-emerald-700 text-white rounded-xl font-bold hover:shadow-xl transition"
        >
          {currentProject ? 'Sauvegarder' : 'Créer le projet'}
        </button>
      </div>
    </form>
  );
};

export default ProjectForm;
