// src/components/Dashboard.js → DESIGN PRO + FONCTIONNEL
import { useState, useEffect } from 'react';
import ProjectList from './ProjectList';
import API from '../services/api';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) return (window.location.href = '/login');

      const [userRes, projectsRes] = await Promise.all([
        API.get('/users/me'),
        API.get('/projects')
      ]);

      setUser(userRes.data);
      setProjects(projectsRes.data || []);
    } catch (err) {
      console.error(err);
      if (err.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/login';
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-600 to-pink-500">
        <div className="text-white text-3xl font-bold">Chargement...</div>
      </div>
    );
  }

  const isProf = user?.role === 'admin';
  const userName = user?.name || user?.username || 'Étudiant';

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-500 via-purple-600 to-pink-500">
      {/* Header magnifique */}
      <header className="bg-white shadow-lg">
        <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-indigo-600 to-purple-600">
            ProjetTracker
          </h1>
          <div className="flex items-center gap-6">
            <nav className="hidden md:flex gap-6 text-gray-700 font-medium">
              <a href="/" className="hover:text-indigo-600 transition">Accueil</a>
              <a href="/dashboard" className="text-indigo-600 font-bold">Tableau de bord</a>
            </nav>
            <button
              onClick={() => {
                localStorage.removeItem('token');
                window.location.href = '/login';
              }}
              className="px-6 py-2 bg-gradient-to-r from-pink-500 to-purple-600 text-white font-semibold rounded-full shadow hover:shadow-xl transition transform hover:scale-105"
            >
              Déconnexion
            </button>
          </div>
        </div>
      </header>

      {/* Hero Section */}
      <div className="pt-20 pb-12 text-center text-white">
        <h1 className="text-5xl md:text-6xl font-extrabold mb-4 drop-shadow-lg">
          {isProf ? 'Espace Professeur' : `Bonjour, ${userName}`}
        </h1>
        <p className="text-xl md:text-2xl opacity-90">
          {isProf
            ? 'Gérez vos projets et suivez les rendus en temps réel'
            : 'Déposez vos comptes-rendus avant la date limite'}
        </p>
      </div>

      {/* Contenu principal */}
      <div className="max-w-7xl mx-auto px-6 -mt-12 pb-20">
        <div className="bg-white/10 backdrop-blur-lg rounded-3xl shadow-2xl p-8 border border-white/20">
          <ProjectList projects={projects} user={user} refreshProjects={fetchData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
