// src/components/ProjectList.js → VERSION FINALE ULTRA SÉCURISÉE
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [studentModalOpen, setStudentModalOpen] = useState(false);
  const [profModalOpen, setProfModalOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  const [fileName, setFileName] = useState('');
  const [file, setFile] = useState(null);

  const isProf = user?.role === 'admin';

  // SÉCURITÉ MAXIMALE : on vérifie TOUT
  const mySubmission = (project) => {
    if (!project || !project.submissions || !user?._id) return null;
    return project.submissions.find(s => 
      s.student && s.student._id?.toString() === user._id.toString()
    );
  };

  const openStudentModal = (project) => {
    if (!project) return;
    setSelectedProject(project);
    setFileName(mySubmission(project)?.customName || '');
    setFile(null);
    setStudentModalOpen(true);
  };

  const openProfModal = (project = null) => {
    setCurrentProject(project);
    setProfModalOpen(true);
  };

  const submitRendu = async () => {
    if (!file) return alert('Veuillez sélectionner un fichier');
    if (!fileName.trim()) return alert('Veuillez donner un nom à votre rendu');

    const formData = new FormData();
    formData.append('file', file);
    formData.append('customName', fileName.trim());

    try {
      await API.post(`/projects/${selectedProject._id}/submit`, formData);
      alert('Compte-rendu déposé avec succès !');
      setStudentModalOpen(false);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors du dépôt');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-indigo-50 p-8">
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openProfModal()}
            className="px-12 py-6 bg-gradient-to-r from-indigo-600 to-purple-700 text-white font-bold text-2xl rounded-full shadow-2xl hover:scale-105 transition"
          >
            + Créer un nouveau projet
          </button>
        </div>
      )}

      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3 max-w-7xl mx-auto">
        {projects.map((project) => (
          <div key={project._id} className="bg-white rounded-3xl shadow-2xl p-10 border border-gray-100">
            <h3 className="text-3xl font-bold text-indigo-900 mb-4">{project.name}</h3>
            <p className="text-gray-600 text-lg mb-6">{project.description || 'Aucune description'}</p>
            <p className="text-gray-700 font-semibold mb-8">
              Deadline : <span className="text-indigo-600 font-bold text-xl">
                {new Date(project.deadline).toLocaleDateString('fr-FR')}
              </span>
            </p>

            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-8 px-10 py-4 bg-blue-600 text-white font-bold rounded-full shadow-lg hover:bg-blue-700 transition"
              >
                Télécharger le sujet (PDF)
              </a>
            )}

            {/* ÉTUDIANT */}
            {!isProf && (
              <div className="mt-8">
                {mySubmission(project) ? (
                  <div className="p-6 bg-green-50 rounded-2xl border-2 border-green-300 text-center">
                    <p className="text-2xl font-bold text-green-800">Rendu déposé !</p>
                    <p className="mt-3 text-lg font-semibold text-indigo-900">
                      {mySubmission(project).customName}
                    </p>
                    <button
                      onClick={() => openStudentModal(project)}
                      className="mt-6 px-10 py-4 bg-indigo-600 text-white font-bold rounded-full hover:bg-indigo-700 transition"
                    >
                      Modifier mon rendu
                    </button>
                  </div>
                ) : (
                  <button
                    onClick={() => openStudentModal(project)}
                    className="w-full py-6 bg-gradient-to-r from-indigo-600 to-purple-700 text-white text-2xl font-bold rounded-2xl shadow-xl hover:shadow-purple-600 transition transform hover:scale-105"
                  >
                    Déposer mon compte-rendu
                  </button>
                )}
              </div>
            )}

            {/* PROFESSEUR */}
            {isProf && (
              <div className="mt-8 p-6 bg-purple-50 rounded-2xl border-2 border-purple-300">
                <p className="text-center text-2xl font-bold text-purple-800 mb-6">
                  {project.submissions?.length || 0} rendu(s) reçu(s)
                </p>
                {project.submissions && project.submissions.length > 0 && (
                  <div className="space-y-3 max-h-64 overflow-y-auto">
                    {project.submissions.map((sub, i) => (
                      <div key={i} className="bg-white p-4 rounded-xl shadow">
                        <p className="font-medium">{sub.customName}</p>
                        <p className="text-sm text-gray-600">
                          Par : {sub.student?.name || sub.student?.email || 'Anonyme'}
                        </p>
                        <a
                          href={`http://localhost:5000/${sub.file}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="inline-block mt-2 text-green-600 font-bold hover:underline"
                        >
                          Télécharger
                        </a>
                      </div>
                    ))}
                  </div>
                )}
                <div className="flex gap-4 mt-8">
                  <button onClick={() => openProfModal(project)} className="flex-1 py-3 bg-indigo-600 text-white font-bold rounded-full">
                    Modifier
                  </button>
                  <button
                    onClick={() => {
                      if (window.confirm('Supprimer ce projet ?')) {
                        API.delete(`/projects/${project._id}`).then(refreshProjects);
                      }
                    }}
                    className="flex-1 py-3 bg-red-600 text-white font-bold rounded-full"
                  >
                    Supprimer
                  </button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal étudiant */}
      <Modal
        isOpen={studentModalOpen}
        onRequestClose={() => setStudentModalOpen(false)}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <h2 className="text-3xl font-bold text-center text-indigo-900 mb-8">
          {mySubmission(selectedProject) ? 'Modifier mon rendu' : 'Déposer mon compte-rendu'}
        </h2>
        <input
          type="text"
          placeholder="Ex: TP1 - Sirine Ouerghemmi.pdf"
          value={fileName}
          onChange={(e) => setFileName(e.target.value)}
          className="w-full px-6 py-4 border-2 border-indigo-300 rounded-xl text-lg mb-6"
        />
        <input
          type="file"
          accept=".pdf,.docx,.zip"
          onChange={(e) => setFile(e.target.files[0])}
          className="w-full p-4 border-2 border-gray-300 rounded-xl mb-8"
        />
        <div className="flex gap-6">
          <button onClick={submitRendu} className="flex-1 py-5 bg-green-600 text-white font-bold text-xl rounded-full">
            {mySubmission(selectedProject) ? 'Mettre à jour' : 'Déposer'}
          </button>
          <button onClick={() => setStudentModalOpen(false)} className="flex-1 py-5 bg-gray-500 text-white font-bold text-xl rounded-full">
            Annuler
          </button>
        </div>
      </Modal>

      {/* Modal prof */}
      <Modal
        isOpen={profModalOpen}
        onRequestClose={() => setProfModalOpen(false)}
        className="bg-white p-10 rounded-3xl shadow-3xl max-w-lg mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <ProjectForm
          currentProject={currentProject}
          closeModal={() => setProfModalOpen(false)}
          refreshProjects={refreshProjects}
        />
      </Modal>
    </div>
  );
};

export default ProjectList;
