import { useState, useEffect } from 'react';
import API from '../services/api';
import socket from '../socket';

const TaskForm = ({ closeModal, currentTask, projectId, setTasks, tasks }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');
  const [completed, setCompleted] = useState(false);
  const [pdf, setPdf] = useState(null);
  const [assignedTo, setAssignedTo] = useState('');

  useEffect(() => {
    if (currentTask) {
      setName(currentTask.name);
      setDescription(currentTask.description);
      setDeadline(new Date(currentTask.deadline).toISOString().split('T')[0]);
      setCompleted(currentTask.completed);
      setAssignedTo(currentTask.assignedTo || '');
    }
  }, [currentTask]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', name);
    formData.append('description', description);
    formData.append('deadline', deadline);
    formData.append('completed', completed);
    formData.append('assignedTo', assignedTo);
    if (pdf) formData.append('pdf', pdf);

    try {
      let updatedTask;
      if (currentTask) {
        const { data } = await API.put(`/tasks/${currentTask._id}`, formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        updatedTask = data;
        setTasks(tasks.map(t => t._id === currentTask._id ? updatedTask : t));
      } else {
        const { data } = await API.post(`/tasks/project/${projectId}`, formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        updatedTask = data;
        setTasks([...tasks, updatedTask]);
      }
      socket.emit('updateProject', { projectId, ...updatedTask });
      closeModal();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Nom" className="mb-4 p-2 border w-full" />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" className="mb-4 p-2 border w-full" />
      <input type="date" value={deadline} onChange={(e) => setDeadline(e.target.value)} className="mb-4 p-2 border w-full" />
      <label>Complétée: <input type="checkbox" checked={completed} onChange={(e) => setCompleted(e.target.checked)} /></label>
      <input type="text" value={assignedTo} onChange={(e) => setAssignedTo(e.target.value)} placeholder="Assigné à (ID user)" className="mb-4 p-2 border w-full" />
      <input type="file" onChange={(e) => setPdf(e.target.files[0])} className="mb-4" />
      <button type="submit" className="bg-green-500 text-white p-2">Sauvegarder</button>
    </form>
  );
};

export default TaskForm;
