// src/components/ProjectList.js
import { useState } from 'react';
import Modal from 'react-modal';
import ProjectForm from './ProjectForm';
import API from '../services/api';

Modal.setAppElement('#root');

const ProjectList = ({ projects = [], user, refreshProjects }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentProject, setCurrentProject] = useState(null);
  const isProf = user?.role === 'admin';

  const openModal = (project = null) => {
    setCurrentProject(project);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentProject(null);
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Supprimer ce projet et tous les fichiers associés ?')) return;

    try {
      await API.delete(`/projects/${id}`);
      refreshProjects();
    } catch (err) {
      alert('Erreur lors de la suppression');
    }
  };

  const handleSubmitFile = async (projectId, file) => {
    const formData = new FormData();
    formData.append('file', file);
    await API.post(`/projects/${projectId}/submit`, formData);
    refreshProjects();
  };

  const hasSubmitted = (project) => 
    project.submissions?.some(s => s.student._id === user._id);

  return (
    <div>
      {/* Bouton Ajouter */}
      {isProf && (
        <div className="text-center mb-12">
          <button
            onClick={() => openModal()}
            className="px-12 py-6 bg-gradient-to-r from-indigo-500 to-purple-600 text-white text-2xl font-bold rounded-3xl shadow-2xl hover:shadow-purple-500 transition"
          >
            + Ajouter un nouveau projet
          </button>
        </div>
      )}

      {/* Liste des projets */}
      <div className="grid gap-10 md:grid-cols-2 lg:grid-cols-3">
        {projects.map((project) => (
          <div key={project._id} className="bg-white rounded-3xl shadow-2xl p-10 border border-gray-100 hover:shadow-purple-200 transition">
            <h3 className="text-3xl font-bold text-indigo-800 mb-4">{project.name}</h3>
            <p className="text-gray-600 mb-4">{project.description || 'Pas de description'}</p>
            <p className="text-lg font-medium text-gray-700 mb-8">
              Deadline : <span className="text-indigo-600">{new Date(project.deadline).toLocaleDateString('fr-FR')}</span>
            </p>

            {/* Sujet PDF */}
            {project.pdf && (
              <a
                href={`http://localhost:5000/${project.pdf}`}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block mb-8 px-10 py-5 bg-gradient-to-r from-blue-600 to-blue-700 text-white font-bold rounded-2xl shadow-lg hover:shadow-blue-600"
              >
                Télécharger le sujet
              </a>
            )}

            {/* Étudiant : dépôt */}
            {/* Étudiant : dépôt + modification + suppression du rendu */}
{!isProf && (
  <div className="mt-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl border-2 border-blue-100">
    {hasSubmitted(project) ? (
      <div className="space-y-5">
        <p className="text-green-700 font-bold text-xl text-center mb-4">
          Rendu déposé !
        </p>

        {/* Affichage du fichier actuel */}
        {project.submissions.find(s => s.student._id === user._id)?.file && (
          <a
            href={`http://localhost:5000/${project.submissions.find(s => s.student._id === user._id).file}`}
            target="_blank"
            rel="noopener noreferrer"
            className="block text-center text-blue-600 underline font-medium"
          >
            Voir mon fichier actuel
          </a>
        )}

        <div className="flex justify-center gap-6">
          {/* Bouton Modifier le rendu */}
          <label className="cursor-pointer">
            <input
              type="file"
              accept=".pdf,.zip,.docx"
              onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
              className="hidden"
            />
            <span className="inline-block px-10 py-4 bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white font-bold rounded-2xl shadow-lg hover:shadow-xl transition-all">
              Modifier mon rendu
            </span>
          </label>

          {/* Bouton Supprimer le rendu */}
          <button
            onClick={() => handleDeleteSubmission(project._id)}
            className="px-10 py-4 bg-gradient-to-r from-red-500 to-pink-600 hover:from-red-600 hover:to-pink-700 text-white font-bold rounded-2xl shadow-lg hover:shadow-xl transition-all"
          >
            Supprimer mon rendu
          </button>
        </div>
      </div>
    ) : (
      <div className="text-center">
        <p className="font-bold text-gray-800 mb-4">Déposez votre compte-rendu :</p>
        <label className="cursor-pointer">
          <input
            type="file"
            accept=".pdf,.zip,.docx"
            onChange={(e) => e.target.files[0] && handleSubmitFile(project._id, e.target.files[0])}
            className="hidden"
          />
          <span className="inline-block px-12 py-5 bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-bold text-xl rounded-3xl shadow-xl hover:shadow-2xl transition-all">
            Déposer mon travail
          </span>
        </label>
      </div>
    )}
  </div>
)}

            {/* Prof : voir les rendus */}
            {isProf && project.submissions?.length > 0 && (
              <div className="bg-purple-50 p-6 rounded-2xl mb-8">
                <p className="font-bold text-purple-800 text-xl mb-3">
                  {project.submissions.length} rendu(s)
                </p>
                {project.submissions.map((s, i) => (
                  <div key={i} className="text-sm bg-white p-3 rounded mb-2 shadow">
                    <strong>{s.student.name}</strong>
                    <a href={`http://localhost:5000/${s.file}`} target="_blank" className="text-blue-600 underline ml-3">
                      Voir le fichier
                    </a>
                  </div>
                ))}
              </div>
            )}

            {/* Boutons Modifier & Supprimer – CENTRÉS */}
            {isProf && (
              <div className="flex justify-center gap-8">
                <button
                  onClick={() => openModal(project)}
                  className="px-10 py-5 bg-gradient-to-r from-blue-50 to-blue-100 hover:from-blue-100 hover:to-blue-200 text-blue-700 font-bold rounded-2xl shadow-lg hover:shadow-2xl transition-all border border-blue-200"
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(project._id)}
                  className="px-10 py-5 bg-gradient-to-r from-purple-50 to-purple-100 hover:from-purple-100 hover:to-purple-200 text-purple-700 font-bold rounded-2xl shadow-lg hover:shadow-2xl transition-all border border-purple-200"
                >
                  Supprimer
                </button>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Modal */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="bg-white p-12 rounded-3xl shadow-2xl max-w-3xl mx-auto outline-none"
        overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50"
      >
        <ProjectForm currentProject={currentProject} closeModal={closeModal} refreshProjects={refreshProjects} />
      </Modal>
    </div>
  );
};

export default ProjectList;
